INSERT INTO `pk_err_code_chk` VALUES ('DT1106', 'ข้อมูลประเภทผู้ได้รับบริการตรวจสภาวะทันตสุขภาพ(DENTTYPE)ไม่ใช่ค่า 1,2,3,4,5 หรือ เป็นค่าว่าง(null)', 'select  hn,cid,concat(pname,\" \",fname,\" \",lname) as ptname ,(select vstdate from vn_stat where vn = dental_care.vn ) as vstdate\r\n    from dental_care\r\n   left join patient on  (select hn from vn_stat where vn = dental_care.vn ) = patient.hn\r\n  where  (dental_care_type_id not in(SELECT dental_care_type_id from dental_care_type) or dental_care_type_id is NULL or dental_care_type_id = \'\')\r\n');
