INSERT INTO `pk_err_code_chk` VALUES ('NE1102', 'จำนวนครั้งที่การเยี่ยมเป็นค่าว่าง Null', '\r\n\r\nselect  cid,concat(pname,\" \",fname,\" \",lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,care_date as vstdate ,\r\nperson_wbc_post_care.care_number,DATEDIFF(person_wbc_post_care.care_date,person.birthdate) as day_cc ,DATEDIFF(CURDATE(),person.birthdate) as day_labor	\r\n				from person_wbc_post_care\r\n				left join person on (select person_id from person_wbc where  person_wbc.person_wbc_id = person_wbc_post_care.person_wbc_id limit 1) = person.person_id\r\n				where (person_wbc_post_care.care_number is null or person_wbc_post_care.care_number = \'\' )\r\n				and  CONCAT(SUBSTR(((year(care_date))+543),3,2),if(LENGTH(MONTH(care_date))=1,concat(\'0\',MONTH(care_date)),MONTH(care_date)))  ');
