INSERT INTO `pk_err_code_chk` VALUES ('NE9901', 'เด็กที่ต้องเยี่ยมหลังคลอดครั้งที่ 1', 'select  cid,concat(pname,\" \",fname,\" \",lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,\'\' as vstdate ,\r\n\'\' as care_number, 0 as day_cc 	,DATEDIFF(CURDATE(),person.birthdate) as day_labor\r\n	from person_wbc 	\r\n	left join person on person_wbc.person_id = person.person_id	\r\n	where person.house_regist_type_id in(1,3) and DATEDIFF(CURDATE(),person.birthdate) > 0 and baby_service1_date is null	\r\n    and DATEDIFF(person_wbc.person_wbc_regdate,person.birthdate) < 8	and  person.birthdate BETWEEN  ');
