INSERT INTO `pk_err_code_chk` VALUES ('PE9215', 'ข้อมูลการศึกษา(EDUCATE) ไม่พบอยู่ในรหัสมาตราฐานของสนย. หรือเป็นค่าว่าง(null)', '\r\nselect person_id,cid,concat(pname,\" \",fname,\" \",lname) as ptname ,age_y ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr   from person \r\nwhere (education not in(select education from education)  or education is null)');
INSERT INTO `pk_err_code_chk` VALUES ('PE9216', 'ข้อมูลสถานะบุคล(Labor Type) ไม่พบอยู่ในรายการของสนย. หรือเป็นค่าว่าง(null)', 'select person_id,cid,concat(pname,\" \",fname,\" \",lname) as ptname ,age_y  \r\n,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr     from person \r\nwhere nationality <> 99 and (person_labor_type_id not in(select person_labor_type_id from person_labor_type) or person_labor_type_id is null)  ');
