INSERT INTO `pk_err_code_chk` VALUES ('PO1104', 'ข้อมูลการตั้งครรภ์ครั้งที่ (GRAVIDA) เป็นค่าว่าง (null)', 'select cid,concat(pname,\" \",fname,\" \",lname) as ptname ,person.person_id ,care_date as vstdate,person_anc.labor_date,\r\nperson_anc_preg_care.preg_care_number as care_number,DATEDIFF(person_anc_preg_care.care_date,person_anc.labor_date) as day_cc \r\n,person_anc.preg_no	, DATEDIFF(CURDATE(),person_anc.labor_date) as date_labor\r\n				from person_anc_preg_care\r\n				left join person_anc on person_anc_preg_care.person_anc_id = person_anc.person_anc_id\r\n				LEFT JOIN person on person_anc.person_id = person.person_id\r\n				 where (person_anc.preg_no is null or person_anc.preg_no = \'\') and vn   \r\n');
INSERT INTO `pk_err_code_chk` VALUES ('PO1105', 'ข้อมูลวันคลอด / วันสิ้นสุดการตั้งครรภ์ (BDATE)ไม่อยู่ในรูปของ YYYYMMDD  หรือเป็นค่าว่าง(null)', 'select cid,concat(pname,\" \",fname,\" \",lname) as ptname ,person.person_id ,care_date as vstdate,person_anc.labor_date,\r\nperson_anc_preg_care.preg_care_number as care_number,DATEDIFF(person_anc_preg_care.care_date,person_anc.labor_date) as day_cc \r\n,person_anc.preg_no		, DATEDIFF(CURDATE(),person_anc.labor_date) as date_labor\r\n			from person_anc_preg_care\r\n				left join person_anc on person_anc_preg_care.person_anc_id = person_anc.person_anc_id\r\n				LEFT JOIN person on person_anc.person_id = person.person_id\r\n				 where (person_anc.labor_date is null or person_anc.labor_date = \'\') and vn  ');
INSERT INTO `pk_err_code_chk` VALUES ('PO1106', 'ข้อมูลวันเดือนปีที่ได้รับการดูแลแม่หลังคลอด (PPCARE) ไม่อยู่ในรูปของ YYYYMMDD หรือเป็นค่าว่าง (null)', '');
