INSERT INTO `pk_err_code_chk` VALUES ('PO9901', 'แม่ที่ต้องเยี่ยมหลังคลอดครั้งที่ 1', 'select cid,concat(pname,\" \",fname,\" \",lname) as ptname ,person.person_id ,\'\' as vstdate,person_anc.labor_date,\r\n\'\' as care_number,0 as day_cc ,person_anc.preg_no , DATEDIFF(CURDATE(),person_anc.labor_date) as date_labor\r\n	from person_anc \r\n	left join person on person_anc.person_id = person.person_id	\r\n	where person.house_regist_type_id in(1,3) and DATEDIFF(CURDATE(),person_anc.labor_date) > 0 and person_anc.post_labor_service1_date is null and DATEDIFF(person_anc.anc_register_date,person_anc.labor_date) <  8 \r\n	and person_anc.labor_date  BETWEEN  ');
INSERT INTO `pk_err_code_chk` VALUES ('PO9902', 'แม่ที่ต้องเยี่ยมหลังคลอดครั้งที่ 2', 'select cid,concat(pname,\" \",fname,\" \",lname) as ptname ,person.person_id ,\'\' as vstdate,person_anc.labor_date,\r\n\'\' as care_number,0 as day_cc ,person_anc.preg_no , DATEDIFF(CURDATE(),person_anc.labor_date) as date_labor\r\n	from person_anc \r\n	left join person on person_anc.person_id = person.person_id	\r\n	where person.house_regist_type_id in(1,3) and DATEDIFF(CURDATE(),person_anc.labor_date) > 7 and person_anc.post_labor_service2_date is null  and DATEDIFF(person_anc.anc_register_date,person_anc.labor_date) <  16 \r\n	and person_anc.labor_date  BETWEEN  ');
INSERT INTO `pk_err_code_chk` VALUES ('PO9903', 'แม่ที่ต้องเยี่ยมหลังคลอดครั้งที่ 3', 'select cid,concat(pname,\" \",fname,\" \",lname) as ptname ,person.person_id ,\'\' as vstdate,person_anc.labor_date,\r\n\'\' as care_number,0 as day_cc ,person_anc.preg_no , DATEDIFF(CURDATE(),person_anc.labor_date) as date_labor\r\n	from person_anc \r\n	left join person on person_anc.person_id = person.person_id	\r\n	where person.house_regist_type_id in(1,3) and DATEDIFF(CURDATE(),person_anc.labor_date) > 15 and person_anc.post_labor_service3_date is null  and DATEDIFF(person_anc.anc_register_date,person_anc.labor_date) <  43   \r\n	and person_anc.labor_date  BETWEEN  ');
