INSERT INTO `pk_err_code_chk` VALUES ('AD1106', 'ข้อมูลสถานที่เกิดอุบัติเหตุ(AEPLACE)ไม่ใช่ค่า 01-11,98 หรือ 99 ', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(select vstdate from vn_stat where vn = er_nursing_detail.vn ) as vstdate\r\n,accident_place_type_id,vn ,(select vsttime from ovst where vn = er_nursing_detail.vn ) as vsttime  \r\nfrom er_nursing_detail \r\nleft join patient on  (select hn from vn_stat where vn = er_nursing_detail.vn ) = patient.hn  \r\nwhere  ( visit_type not in(select visit_type from er_nursing_visit_type) or visit_type is null )  and  er_accident_type_id is not null   and  \r\n ( accident_place_type_id  is null  or accident_place_type_id not in(SELECT accident_place_type_id from accident_place_type ) ) ', '2020-04-21 11:09:22');
