INSERT INTO `pk_err_code_chk` VALUES ('AD1112', 'ข้อมูลการใส่ splint/ slab(SPLINT) ไม่ใช่ค่า 1,2 หรือ 3', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(select vstdate from vn_stat where vn = er_nursing_detail.vn ) as vstdate ,\r\n(select vsttime from ovst where vn = er_nursing_detail.vn ) as vsttime \r\n    from er_nursing_detail \r\nleft join patient on  (select hn from vn_stat where vn = er_nursing_detail.vn ) = patient.hn\r\n     where er_accident_type_id is not null and (accident_splint_type_id is null or accident_splint_type_id not in(\'1\',\'2\',\'3\')) ', '2020-04-21 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('AD1113', 'ข้อมูลการให้น้ำเกลือ(FLUID)ไม่ใช่ค่า 1,2 หรือ 3', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(select vstdate from vn_stat where vn = er_nursing_detail.vn ) as vstdate ,\r\n(select vsttime from ovst where vn = er_nursing_detail.vn ) as vsttime \r\n      from er_nursing_detail \r\nleft join patient on  (select hn from vn_stat where vn = er_nursing_detail.vn ) = patient.hn\r\nwhere er_accident_type_id is not null and (accident_fluid_type_id is null or accident_fluid_type_id not in(\'1\',\'2\',\'3\')) ', '2020-04-21 11:09:22');
