INSERT INTO `pk_err_code_chk` VALUES ('AD2120', 'ระดับความรู้สึกตัววัดจากการตอบสนองของตา(COMA_EYE) ค่าไม่ตรงกับมาตรฐาน    ', '\r\nselect  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(select vstdate from vn_stat where vn = er_nursing_detail.vn ) as vstdate ,\r\n(select vsttime from ovst where vn = er_nursing_detail.vn ) as vsttime \r\n   from er_nursing_detail \r\nleft join patient on  (select hn from vn_stat where vn = er_nursing_detail.vn ) = patient.hn\r\nwhere er_accident_type_id is not null and (gcs_e is null or gcs_e not BETWEEN \'1\' and \'4\') ', '2020-04-21 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('AD2121', 'ข้อมูล ระดับความรู้สึกทางด้านการพูด (COMA_SPEAK) ค่าไม่ตรงกับมาตรฐาน', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(select vstdate from vn_stat where vn = er_nursing_detail.vn ) as vstdate ,\r\n(select vsttime from ovst where vn = er_nursing_detail.vn ) as vsttime \r\n   \r\n from er_nursing_detail \r\nleft join patient on  (select hn from vn_stat where vn = er_nursing_detail.vn ) = patient.hn\r\nwhere er_accident_type_id is not null and (gcs_v is null or gcs_v not BETWEEN \'1\' and \'5\') ', '2020-04-21 11:09:22');
