INSERT INTO `pk_err_code_chk` VALUES ('AM1101', 'วันที่ Admit มากกว่าวัน Discharge', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.regdate as vstdate, ipt.regtime as vsttime, ipt.an , ipt.vn  \r\nfrom ipt  \r\nleft join patient on  ipt.hn = patient.hn \r\nwhere  regdate >= dchdate and regtime >= dchtime  and ipt.vn ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('AM1200', 'ไม่มีข้อมูลน้ำหนัก (body_weight)', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.regdate as vstdate, ipt.regtime as vsttime, ipt.an , ipt.vn  \r\nfrom ipt  \r\nleft join patient on  ipt.hn = patient.hn \r\nwhere  (bw = \'\' or bw is null or  bw =0 ) and concat(mid((year(ipt.dchdate)+543),3,2),LPAD(month(ipt.dchdate),2,\'0\')) ', '2018-05-04 11:05:22');
INSERT INTO `pk_err_code_chk` VALUES ('AM1201', 'ไม่มีข้อมูลส่วนสูง (body_height)', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.regdate as vstdate, ipt.regtime as vsttime, ipt.an , ipt.vn  \r\nfrom ipt  \r\nleft join patient on  ipt.hn = patient.hn \r\nwhere (body_height = \'\' or body_height is null or body_height =0) and concat(mid((year(ipt.dchdate)+543),3,2),LPAD(month(ipt.dchdate),2,\'0\'))    ', '2018-05-04 11:10:54');
INSERT INTO `pk_err_code_chk` VALUES ('AM2112', 'ไม่มีมีข้อมูลส่งต่อ แต่การจำหน่ายเป็นส่งต่อ(DISCHTYPE)', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.regdate as vstdate, ipt.regtime as vsttime, ipt.an , ipt.vn  \r\nfrom ipt  \r\nleft join patient on  ipt.hn = patient.hn \r\nleft JOIN referout on ipt.an = referout.vn\r\nwhere (ipt.dchtype in(\'04\',\'04\')) and referout.refer_number is null and concat(mid((year(ipt.dchdate)+543),3,2),LPAD(month(ipt.dchdate),2,\'0\'))  ', '2018-07-09 15:42:16');
INSERT INTO `pk_err_code_chk` VALUES ('AM2113', 'มีข้อมูลส่งต่อ แต่การจำหน่ายไม่ใช่เป็นส่งต่อ(DISCHTYPE)', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname,\' [RN:\',referout.refer_number,\']\') as ptname ,ipt.regdate as vstdate, ipt.regtime as vsttime, ipt.an , ipt.vn  \r\nfrom ipt  \r\nleft join patient on  ipt.hn = patient.hn \r\nINNER JOIN referout on ipt.an = referout.vn\r\nwhere (rfrolct <> \"\" or rfrolct is not null) and dchtype <> \'04\' and concat(mid((year(ipt.dchdate)+543),3,2),LPAD(month(ipt.dchdate),2,\'0\'))  ', '2018-07-05 13:45:21');
INSERT INTO `pk_err_code_chk` VALUES ('AM2114', 'วิธีการจำหน่ายผู้ป่วย (DISCHTYPE) ค่าไม่ตรงกับมาตรฐาน', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate, ovst.vsttime , ovst.an , ovst.vn\r\n				from ipt \r\n			left join patient on ipt.hn = patient.hn\r\n			left join ovst on ipt.an = ovst.an\r\n			inner join an_stat on ipt.an = an_stat.an\r\n				where  (ipt.dchtype is null or ipt.dchtype = \'\' or ipt.dchtype not in(SELECT dchtype from dchtype)) and concat(mid((year(ipt.dchdate)+543),3,2),LPAD(month(ipt.dchdate),2,0))   ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('AM2115', 'สถานภาพการจำหน่ายผู้ป่วย (DISCHSTATUS) ค่าไม่ตรงกับมาตรฐาน', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate, ovst.vsttime , ovst.an , ovst.vn\r\n				from ipt \r\n			left join patient on ipt.hn = patient.hn\r\n			left join ovst on ipt.an = ovst.an\r\n			inner join an_stat on ipt.an = an_stat.an\r\n				where  (ipt.dchstts is null or ipt.dchstts = \'\' or ipt.dchstts not in(SELECT dchstts from dchstts)) and concat(mid((year(ipt.dchdate)+543),3,2),LPAD(month(ipt.dchdate),2,0))   ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('AM9101', 'ไม่พบข้อมูลการวินิจฉัย (DIAGNOSISS_IPD)', '\r\nselect  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate, ovst.vsttime , ovst.an , ovst.vn\r\n   from ipt \r\n    left join patient on ipt.hn = patient.hn\r\n     left join ovst on ipt.an = ovst.an\r\n     inner join an_stat on ipt.an = an_stat.an\r\n      where  an_stat.pdx  = \'\'  and concat(mid((year(ipt.dchdate)+543),3,2),LPAD(month(ipt.dchdate),2,0))   ', '2016-07-08 11:09:22');
