INSERT INTO `pk_err_code_chk` VALUES ('AN9902', 'ไม่มีข้อมูล ครรภ์ที่ (GRAVIDA) ', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ps_service.anc_service_date as vstdate ,ps_anc.preg_no ,ps_service.pa_week \r\n				from person_anc_service ps_service\r\n				left JOIN person_anc ps_anc on ps_service.person_anc_id = ps_anc.person_anc_id\r\n        left join patient on (select cid from person where person_id = ps_anc.person_id) = patient.cid\r\n  where   ps_service.anc_service_type_id = 1 and ps_anc.preg_no is null\r\n  and ps_service.vn  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('AN9903', 'ข้อมูลครรภ์ที่ซ้ำซ้อน', '								select  patient_hn as hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,anc_lasthan1.anc_regist as vstdate ,anc_lasthan1.preg_no ,person_anc_service.pa_week \r\n								from person_anc\r\n								INNER JOIN (SELECT person_id,preg_no	,max(anc_register_date) as anc_regist	,max(person_anc_id)	person_anc_id						\r\n								from person_anc \r\n								GROUP BY  person_id ,preg_no\r\n								HAVING count(person_id) > 1 ) anc_lasthan1 on person_anc.person_id = anc_lasthan1.person_id\r\n                left join  person on person_anc.person_id = person.person_id\r\n								LEFT JOIN person_anc_service on anc_lasthan1.person_anc_id = person_anc_service.person_anc_id \r\n                where  concat(RIGHT((DATE_FORMAT(person_anc.anc_register_date ,\'%Y\') +543),2),DATE_FORMAT(person_anc.anc_register_date ,\'%m\'))  ', '2016-08-31 14:13:40');
INSERT INTO `pk_err_code_chk` VALUES ('AN9904', 'หญิงตั้งครรภ์ที่ไม่ได้รับการตรวจฟัน(Dental Care)', 'SELECT\r\n	hn,\r\n	cid,\r\n	concat(pname, \' \', fname, \' \', lname) AS ptname,\r\n	person_anc.anc_register_date AS vstdate,\r\n	anc_dental.preg_no,\r\n	TIMESTAMPDIFF(\r\n		WEEK,\r\n		person_anc.lmp,\r\n		person_anc.anc_register_date\r\n	) pa_week,\r\n	person_anc.discharge\r\nFROM\r\n	person_anc\r\nINNER JOIN (\r\n	SELECT\r\n		person_anc.person_id,\r\n		person_anc.preg_no\r\n	FROM\r\n		person_anc\r\n	LEFT JOIN (\r\n		SELECT\r\n			person.person_id\r\n		FROM\r\n			dental_care\r\n		LEFT JOIN ovst ON dental_care.vn = ovst.vn\r\n		LEFT JOIN person ON ovst.hn = person.patient_hn\r\n	) person_dental ON person_anc.person_id = person_dental.person_id\r\n	WHERE\r\n		person_dental.person_id IS NULL\r\n	AND person_anc.discharge <> \'Y\'\r\n) anc_dental ON person_anc.person_id = anc_dental.person_id\r\nLEFT JOIN patient ON (\r\n	SELECT\r\n		cid\r\n	FROM\r\n		person\r\n	WHERE\r\n		person_id = anc_dental.person_id\r\n) = patient.cid\r\nWHERE\r\n	out_region = \'N\'\r\nAND person_anc.labour_hospcode = (SELECT hospitalcode from opdconfig) and  concat(\r\n	RIGHT (\r\n		(\r\n			DATE_FORMAT(\r\n				person_anc.anc_register_date,\r\n				\'%Y\'\r\n			) + 543\r\n		),\r\n		2\r\n	),\r\n	DATE_FORMAT(\r\n		person_anc.anc_register_date,\r\n		\'%m\'\r\n	)\r\n)', '2020-11-24 14:22:28');
INSERT INTO `pk_err_code_chk` VALUES ('AN9905', 'ผู้รับบริการฝากครรภ์(ในเขต) ขาดนัด ** ต้องติดตาม', 'select  patient.hn,patient.cid,concat(pname,\' \',fname,\' \',lname) as ptname ,max(person_anc_service.anc_service_date) as vstdate ,person_anc.preg_no \r\n,person_anc_service.pa_week , person_anc.labor_date,\r\n\r\n if(((DATEDIFF(CURDATE(),oapp.nextdate)/7) > 0 and person_anc_other_precare.precare_date is null and person_anc.labor_date is  null),\"X\",\"O\") as chk \r\n,oapp.nextdate\r\nfrom person_anc\r\nLEFT JOIN person_anc_service on  person_anc.person_anc_id = person_anc_service.person_anc_id \r\nLEFT JOIN oapp on (SELECT vn from person_anc_service where person_anc_id = person_anc.person_anc_id ORDER BY person_anc_service.vn desc limit 1) = oapp.vn and oapp.clinic = \'007\'\r\nLEFT JOIN person_anc_other_precare on person_anc.person_anc_id = person_anc_other_precare.person_anc_id and person_anc_other_precare.precare_date > oapp.nextdate\r\nleft join patient on (select cid from person where person_id = person_anc.person_id) = patient.cid\r\nwhere person_anc_service.anc_service_type_id = 1 and person_anc.discharge <> \'Y\' and person_anc.out_region = \'N\'\r\nand oapp.nextdate < CURDATE()\r\nGROUP BY person_anc.person_anc_id , person_anc.person_id, person_anc.preg_no \r\nHAVING chk = \'X\'\r\nORDER BY person_anc.person_id ,person_anc.preg_no \r\n  ', '2022-07-06 15:20:55');
INSERT INTO `pk_err_code_chk` VALUES ('AN9971', 'การฝากครรภ์ครั้งที่ 1 (ไม่อยู่ในช่วงคุณภาพ 5-12 week หรือเป็นค่าว่าง null )', 'SELECT\r\n	hn,\r\n	cid,\r\n	concat(pname, \' \', fname, \' \', lname) AS ptname,\r\n	person_anc.anc_register_date AS vstdate,\r\n	person_anc.preg_no,\r\n	anc_chk.wpa_now AS pa_week\r\nFROM\r\n	person_anc\r\nINNER JOIN (\r\n	SELECT\r\n		*,\r\n		TIMESTAMPDIFF(\r\n			WEEK,\r\n			person_anc.lmp,\r\n			CURDATE()\r\n		) AS wpa_now,\r\n		(\r\n			CASE\r\n			WHEN TIMESTAMPDIFF(\r\n				WEEK,\r\n				person_anc.lmp,\r\n				person_anc.pre_labor_service1_date\r\n			) NOT BETWEEN (\r\n				SELECT\r\n					week_min_quality\r\n				FROM\r\n					person_anc_preg_week\r\n				WHERE\r\n					person_anc_preg_week_id = 1\r\n			)\r\n			AND (\r\n				SELECT\r\n					week_max_quality\r\n				FROM\r\n					person_anc_preg_week\r\n				WHERE\r\n					person_anc_preg_week_id = 1\r\n			) THEN\r\n				(\r\n					SELECT\r\n\r\n					IF (\r\n						count(person_anc_id) > 0,\r\n						\'Yes\',\r\n						(\r\n							SELECT\r\n\r\n							IF (\r\n								count(person_anc1.person_anc_id) > 0,\r\n								\'Yes\',\r\n								\'NO\'\r\n							)\r\n							FROM\r\n								person_anc_other_precare\r\n							INNER JOIN person_anc person_anc1 ON person_anc_other_precare.person_anc_id = person_anc1.person_anc_id\r\n							WHERE\r\n								precare_no = 1\r\n							AND\r\n							IF (\r\n								TIMESTAMPDIFF(\r\n									WEEK,\r\n									person_anc1.lmp,\r\n									person_anc_other_precare.precare_date\r\n								) BETWEEN (\r\n									SELECT\r\n										week_min_quality\r\n									FROM\r\n										person_anc_preg_week\r\n									WHERE\r\n										person_anc_preg_week_id = 1\r\n								)\r\n								AND (\r\n									SELECT\r\n										week_max_quality\r\n									FROM\r\n										person_anc_preg_week\r\n									WHERE\r\n										person_anc_preg_week_id = 1\r\n								),\r\n								\'Y\',\r\n								\'N\'\r\n							) = \'Y\'\r\n							AND person_anc1.person_anc_id = person_anc.person_anc_id\r\n						)\r\n					)\r\n					FROM\r\n						person_anc_service\r\n					WHERE\r\n						anc_service_number = 1\r\n					AND\r\n					IF (\r\n						TIMESTAMPDIFF(\r\n							WEEK,\r\n							person_anc.lmp,\r\n							person_anc_service.anc_service_date\r\n						) BETWEEN (\r\n							SELECT\r\n								week_min_quality\r\n							FROM\r\n								person_anc_preg_week\r\n							WHERE\r\n								person_anc_preg_week_id = 1\r\n						)\r\n						AND (\r\n							SELECT\r\n								week_max_quality\r\n							FROM\r\n								person_anc_preg_week\r\n							WHERE\r\n								person_anc_preg_week_id = 1\r\n						),\r\n						\'Y\',\r\n						\'N\'\r\n					) = \'Y\'\r\n					AND person_anc_id = person_anc.person_anc_id\r\n				)\r\n			ELSE\r\n\r\n			IF (\r\n				person_anc.pre_labor_service1_date IS NULL,\r\n				\'NULL\',\r\n				\'Yes\'\r\n			)\r\n			END\r\n		) AS week_anc\r\n	FROM\r\n		person_anc\r\n	WHERE\r\n		discharge <> \'Y\'\r\n	AND out_region <> \'Y\'\r\n	AND (\r\n		TIMESTAMPDIFF(\r\n			WEEK,\r\n			person_anc.lmp,\r\n			CURDATE()\r\n		) > (\r\n			SELECT\r\n				week_max\r\n			FROM\r\n				person_anc_preg_week\r\n			WHERE\r\n				person_anc_preg_week_id = 1\r\n		)\r\n	)\r\n	HAVING\r\n		week_anc = \'NO\'\r\n	OR person_anc.pre_labor_service1_date IS NULL\r\n) anc_chk ON person_anc.person_anc_id = anc_chk.person_anc_id\r\nLEFT JOIN patient ON (\r\n	SELECT\r\n		cid\r\n	FROM\r\n		person\r\n	WHERE\r\n		person_id = person_anc.person_id\r\n) = patient.cid\r\nWHERE  person_anc.labour_hospcode = (SELECT hospitalcode from opdconfig) and \r\n	concat(\r\n		RIGHT (\r\n			(\r\n				DATE_FORMAT(\r\n					person_anc.anc_register_date,\r\n					\'%Y\'\r\n				) + 543\r\n			),\r\n			2\r\n		),\r\n		DATE_FORMAT(\r\n			person_anc.anc_register_date,\r\n			\'%m\'\r\n		)\r\n	)', '2020-11-24 13:51:14');
