n						)\r\n					)\r\n					FROM\r\n						person_anc_service\r\n					WHERE\r\n						anc_service_number = 2\r\n					AND\r\n					IF (\r\n						TIMESTAMPDIFF(\r\n							WEEK,\r\n							person_anc.lmp,\r\n							person_anc_service.anc_service_date\r\n						) BETWEEN (\r\n							SELECT\r\n								week_min_quality\r\n							FROM\r\n								person_anc_preg_week\r\n							WHERE\r\n								person_anc_preg_week_id = 2\r\n						)\r\n						AND (\r\n							SELECT\r\n								week_max_quality\r\n							FROM\r\n								person_anc_preg_week\r\n							WHERE\r\n								person_anc_preg_week_id = 2\r\n						),\r\n						\'Y\',\r\n						\'N\'\r\n					) = \'Y\'\r\n					AND person_anc_id = person_anc.person_anc_id\r\n				)\r\n			ELSE\r\n\r\n			IF (\r\n				person_anc.pre_labor_service2_date IS NULL,\r\n				\'NULL\',\r\n				\'Yes\'\r\n			)\r\n			END\r\n		) AS week_anc\r\n	FROM\r\n		person_anc\r\n	WHERE\r\n		discharge <> \'Y\'\r\n	AND out_region <> \'Y\'\r\n	AND (\r\n		TIMESTAMPDIFF(\r\n			WEEK,\r\n			person_anc.lmp,\r\n			CURDATE()\r\n		) > (\r\n			SELECT\r\n				week_max\r\n			FROM\r\n				person_anc_preg_week\r\n			WHERE\r\n				person_anc_preg_week_id = 2\r\n		)\r\n	)\r\n	HAVING\r\n		week_anc = \'NO\'\r\n	OR person_anc.pre_labor_service2_date IS NULL\r\n) anc_chk ON person_anc.person_anc_id = anc_chk.person_anc_id\r\nLEFT JOIN patient ON (\r\n	SELECT\r\n		cid\r\n	FROM\r\n		person\r\n	WHERE\r\n		person_id = person_anc.person_id\r\n) = patient.cid\r\nWHERE person_anc.labour_hospcode = (SELECT hospitalcode from opdconfig) and \r\n	concat(\r\n		RIGHT (\r\n			(\r\n				DATE_FORMAT(\r\n					person_anc.anc_register_date,\r\n					\'%Y\'\r\n				) + 543\r\n			),\r\n			2\r\n		),\r\n		DATE_FORMAT(\r\n			person_anc.anc_register_date,\r\n			\'%m\'\r\n		)\r\n	)', '2020-11-24 13:51:20');
INSERT INTO `pk_err_code_chk` VALUES ('AN9973', 'การฝากครรภ์ครั้งที่ 3 (ไม่อยู่ในช่วงคุณภาพ 24-28 week หรือเป็นค่าว่าง null )', 'SELECT\r\n	hn,\r\n	cid,\r\n	concat(pname, \' \', fname, \' \', lname) AS ptname,\r\n	person_anc.anc_register_date AS vstdate,\r\n	person_anc.preg_no,\r\n	anc_chk.wpa_now AS pa_week\r\nFROM\r\n	person_anc\r\nINNER JOIN (\r\n	SELECT\r\n		*,\r\n		TIMESTAMPDIFF(\r\n			WEEK,\r\n			person_anc.lmp,\r\n			CURDATE()\r\n		) AS wpa_now,\r\n		(\r\n			CASE\r\n			WHEN TIMESTAMPDIFF(\r\n				WEEK,\r\n				person_anc.lmp,\r\n				person_anc.pre_labor_service3_date\r\n			) NOT BETWEEN (\r\n				SELECT\r\n					week_min_quality\r\n				FROM\r\n					person_anc_preg_week\r\n				WHERE\r\n					person_anc_preg_week_id = 3\r\n			)\r\n			AND (\r\n				SELECT\r\n					week_max_quality\r\n				FROM\r\n					person_anc_preg_week\r\n				WHERE\r\n					person_anc_preg_week_id = 3\r\n			) THEN\r\n				(\r\n					SELECT\r\n\r\n					IF (\r\n						count(person_anc_id) > 0,\r\n						\'Yes\',\r\n						(\r\n							SELECT\r\n\r\n							IF (\r\n								count(person_anc1.person_anc_id) > 0,\r\n								\'Yes\',\r\n								\'NO\'\r\n							)\r\n							FROM\r\n								person_anc_other_precare\r\n							INNER JOIN person_anc person_anc1 ON person_anc_other_precare.person_anc_id = person_anc1.person_anc_id\r\n							WHERE\r\n								precare_no = 3\r\n							AND\r\n							IF (\r\n								TIMESTAMPDIFF(\r\n									WEEK,\r\n									person_anc1.lmp,\r\n									person_anc_other_precare.precare_date\r\n								) BETWEEN (\r\n									SELECT\r\n										week_min_quality\r\n									FROM\r\n										person_anc_preg_week\r\n									WHERE\r\n										person_anc_preg_week_id = 3\r\n								)\r\n								AND (\r\n									SELECT\r\n										week_max_quality\r\n									FROM\r\n										person_anc_preg_week\r\n									WHERE\r\n										person_anc_preg_week_id = 3\r\n								),\r\n								\'Y\',\r\n								\'N\'\r\n							) = \'Y\'\r\n							AND person_anc1.person_anc_id = person_anc.person_anc_id\r\n						)\r\n					)\r\n					FROM\r\n						person_anc_service\r\n					WHERE\r\n						anc_service_number = 3\r\n					AND\r\n					IF (\r\n						TIMESTAMPDIFF(\r\n							WEEK,\r\n							person_anc.lmp,\r\n							person_anc_service.anc_service_date\r\n						) BETWEEN (\r\n							SELECT\r\n								week_min_quality\r\n							FROM\r\n								person_anc_preg_week\r\n							WHERE\r\n								person_anc_preg_week_id = 3\r\n						)\r\n						AND (\r\n							SELECT\r\n								week_max_quality\r\n							FROM\r\n								person_anc_preg_week\r\n							WHERE\r\n								person_anc_preg_week_id = 3\r\n						),\r\n						\'Y\',\r\n						\'N\'\r\n					) = \'Y\'\r\n					AND person_anc_id = person_anc.person_anc_id\r\n				)\r\n			ELSE\r\n\r\n			IF (\r\n				person_anc.pre_labor_service3_date IS NULL,\r\n				\'NULL\',\r\n				\'Yes\'\r\n			)\r\n			END\r\n		) AS week_anc\r\n	FROM\r\n		person_anc\r\n	WHERE\r\n		discharge <> \'Y\'\r\n	AND out_region <> \'Y\'\r\n	AND (\r\n		TIMESTAMPDIFF(\r\n			WEEK,\r\n			person_anc.lmp,\r\n			CURDATE()\r\n		) > (\r\n			SELECT\r\n				week_max\r\n			FROM\r\n				person_anc_preg_week\r\n			WHERE\r\n				person_anc_preg_week_id = 3\r\n		)\r\n	)\r\n	HAVING\r\n		week_anc = \'NO\'\r\n	OR person_anc.pre_labor_service3_date IS NULL\r\n) anc_chk ON person_anc.person_anc_id = anc_chk.person_anc_id\r\nLEFT JOIN patient ON (\r\n	SELECT\r\n		cid\r\n	FROM\r\n		person\r\n	WHERE\r\n		person_id = person_anc.person_id\r\n) = patient.cid\r\nWHERE person_anc.labour_hospcode = (SELECT hospitalcode from opdconfig) and \r\n	concat(\r\n		RIGHT (\r\n			(\r\n				DATE_FORMAT(\r\n					person_anc.anc_register_date,\r\n					\'%Y\'\r\n				) + 543\r\n			),\r\n			2\r\n		),\r\n		DATE_FORMAT(\r\n			person_anc.anc_register_date,\r\n			\'%m\'\r\n		)\r\n	)', '2020-11-24 13:58:34');
