INSERT INTO `pk_err_code_chk` VALUES ('AN9981', 'ข้อมูลรับบริการฝากครรภ์ในหญิงที่คลอดแล้วภายใน 30 วัน', ' select (SELECT concat(vstdate) from ovstdiag where vstdate > person_anc.labor_date  and ovstdiag.icd10 in(\'Z348\',\'Z340\',\'Z350\',\'Z356\',\'Z358\',\'Z357\')\r\n      and DATEDIFF(vstdate,person_anc.labor_date) < 30 and hn = person.patient_hn  limit 1) as vstdate ,person.patient_hn as \'hn\',cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person_anc.preg_no , \'\' as \'pa_week\' \r\n   FROM person_anc  \r\n   LEFT JOIN person on person_anc.person_id = person.person_id\r\n   where labor_date is not null and labor_status_id not in(1,4) \r\n   and (SELECT concat(vstdate ,\'::\',icd10) from ovstdiag where vstdate > person_anc.labor_date  and ovstdiag.icd10 in(\'Z348\',\'Z340\',\'Z350\',\'Z356\',\'Z358\',\'Z357\')\r\n      and DATEDIFF(vstdate,person_anc.labor_date) < 30 and hn = person.patient_hn  limit 1) is not null \r\n   and concat(RIGHT((DATE_FORMAT(person_anc.labor_date ,\'%Y\') +543),2),DATE_FORMAT(person_anc.labor_date ,\'%m\'))  ', '2017-02-02 09:55:05');
INSERT INTO `pk_err_code_chk` VALUES ('AN9982', 'ข้อมูลฝากครรภ์เกิน 42 สัปดาห์ แต่สถานะเป็นยังไม่คลอด', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ps_anc.anc_register_date as vstdate ,ps_anc.preg_no \r\n ,TIMESTAMPDIFF(week,ps_anc.lmp,CURDATE()) as pa_week  \r\nfrom person_anc ps_anc\r\nleft join patient on (select cid from person where person_id = ps_anc.person_id) = patient.cid\r\nwhere   TIMESTAMPDIFF(week,ps_anc.lmp,CURDATE()) >42 and ps_anc.out_region =\'N\' and ps_anc.labor_status_id = \'1\'\r\nand ps_anc.discharge <> \'Y\'  ', '2017-09-13 10:56:23');
INSERT INTO `pk_err_code_chk` VALUES ('AN9990', 'ข้อมูลไม่ได้อยู่ในช่วงปีงบประมาณ', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ps_service.anc_service_date as vstdate ,ps_anc.preg_no ,ps_service.pa_week \r\n      from person_anc_service ps_service\r\n       left join person_anc ps_anc on ps_service.person_anc_id = ps_anc.person_anc_id\r\n       left join patient on (select cid from person where person_id = ps_anc.person_id) = patient.cid\r\n				where   ps_service.anc_service_type_id = 1 \r\n               and (\r\n                             ps_anc.anc_register_date < (case when MONTH(ps_service.anc_service_date) <= 9 THEN\r\n                                                     (concat((year(ps_service.anc_service_date)-1),\'-10-01\'))\r\n                                                            else (concat(year(ps_service.anc_service_date),\'-10-01\')) end )  \r\n                            or\r\n                                ps_anc.anc_register_date > (case when MONTH(ps_service.anc_service_date) <= 9 THEN\r\n                                                     (concat(year(ps_service.anc_service_date),\'-09-30\' ))\r\n                                                            else (concat((year(ps_service.anc_service_date)+1) ,\'-09-30\' )) end )  \r\n                        )  \r\n								and ps_service.vn ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('AN9991', 'ผู้รับบริการฝากครรภ์แต่ผลวินิจฉัยไม่ใช่(Z340,Z348)', 'select  patient.hn,patient.cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ps_service.anc_service_date as vstdate ,ps_anc.preg_no \r\n,ps_service.pa_week  , vn_stat.pdx   \r\nfrom person_anc_service ps_service\r\nleft JOIN person_anc ps_anc on ps_service.person_anc_id = ps_anc.person_anc_id\r\nLEFT JOIN vn_stat on ps_service.vn = vn_stat.vn\r\nleft join patient on (select cid from person where person_id = ps_anc.person_id) = patient.cid\r\nwhere   ps_service.anc_service_type_id = 1 and vn_stat.pdx not in(\"Z348\",\"Z340\") and ps_service.vn  ', '2022-11-15 16:24:48');
INSERT INTO `pk_err_code_chk` VALUES ('AR9901', 'เป็นข้อมูลที่อยู่ในเขตรับผิดชอบอยู่แล้ว', 'SELECT person.person_id,person.cid , CONCAT(person.pname,person.fname,\' \',person.lname) as pt_name ,\r\n CONCAT( house.address , \' หมู่ \',house.village_id -1 ) as hous_addr , (year(CURDATE())-year(person.birthdate)) as age_y,\r\n CONCAT( house_old.address , \' หมู่ \',(house_old.village_id -1) ) as house_old , \r\nCONCAT( house_new.address , \' หมู่ \',(house_new.village_id -1) ) as house_new\r\nfrom person\r\nLEFT JOIN patient on person.cid = patient.cid\r\nLEFT JOIN village on person.village_id = village.village_id\r\nleft join house on person.house_id = house.house_id\r\nLEFT JOIN  person_house_history on person.person_id = person_house_history.person_id \r\nLEFT JOIN house house_old on person_house_history.old_house_id = house_old.house_id\r\nLEFT JOIN house house_new on person_house_history.new_house_id = house_new.house_id\r\ninner JOIN (SELECT person.person_id from patient \r\n                    INNER JOIN person on patient.cid = person.cid \r\n                    LEFT JOIN person_address on person.person_id = person_address.person_id\r\n                    where person.house_regist_type_id in(\'4\') and concat(patient.chwpart ,patient.amppart, patient.tmbpart) = (SELECT concat(chwpart,amppart,tmbpart) from hospcode where hospcode = (SELECT  hospitalcode FROM opdconfig))\r\n                    and person_address.person_id is NULL and year(person.last_update) <= year(CURDATE()) \r\n                    UNION\r\n                    SELECT person.person_id from person \r\n                    LEFT JOIN person_address on person.person_id = person_address.person_id\r\n                    where person.house_regist_type_id in(\'3\') and person_address.person_id is null \r\n										UNION\r\n										SELECT person.person_id from person \r\n										LEFT JOIN person_address on person.person_id = person_address.person_id\r\n										where person.house_regist_type_id in(\'1\') and person_address.chwpart = \'42\'\r\n										and person_address.amppart = \'12\' and person_address.tmbpart = \'03\'	) person_chk on person.person_id = person_chk.person_id\r\nGROUP BY person_id', '2018-11-27 13:23:18');
