INSERT INTO `pk_err_code_chk` VALUES ('CH9901', 'ข้อมูล | บช.1 -> คลีนิกพิเศษ (DM+HT)', 'SELECT person.person_id,person.cid , CONCAT(person.pname,person.fname,\' \',person.lname) as pt_name ,\r\n CONCAT( house.address , \' หมู่ \',village.village_moo ) as hous_addr ,  person_chronic.begin_year , (year(CURDATE())-year(person.birthdate)) as age_y, \r\n if(person_chronic.clinic=\'002\',\'HT\',if(person_chronic.clinic=\'001\',\'DM\',\"\")) as clinic  ,person_chronic.regdate  \r\nfrom person_chronic \r\nleft join person on person_chronic.person_id = person.person_id\r\nLEFT JOIN patient on person.cid = patient.cid\r\nleft join clinicmember on patient.hn = clinicmember.hn\r\nLEFT JOIN village on person.village_id = village.village_id\r\nleft join house on person.house_id = house.house_id\r\nwhere clinicmember.hn is null and person.person_discharge_id <>\'1\'\r\nand person_chronic.clinic in(\'002\',\'001\')  ', '2017-08-10 15:29:46');
INSERT INTO `pk_err_code_chk` VALUES ('CH9902', 'ข้อมูล | คลีนิกพิเศษ ->  บช.1  (DM+HT)', ' SELECT person.person_id,person.cid , CONCAT(person.pname,person.fname,\' \',person.lname) as pt_name ,\r\n CONCAT( house.address , \' หมู่ \',village.village_moo ) as hous_addr ,  clinicmember.begin_year , (year(CURDATE())-year(person.birthdate)) as age_y, \r\n if(clinicmember.clinic=\'002\',\'HT\',if(clinicmember.clinic=\'001\',\'DM\',\"\")) as clinic  ,clinicmember.regdate  \r\nfrom clinicmember \r\nleft JOIN patient on clinicmember.hn = patient.hn\r\nleft JOIN person on patient.cid = person.cid\r\nLEFT JOIN person_chronic on person.person_id = person_chronic.person_id\r\nLEFT JOIN village on person.village_id = village.village_id\r\nleft join house on person.house_id = house.house_id\r\nwhere clinicmember.clinic in(\'001\',\'002\') and person.village_id <> 1 and person.person_discharge_id <> 1 \r\nand clinicmember.clinic_member_status_id <> 3\r\nand person_chronic.person_id is null ', '2017-08-10 16:03:49');
INSERT INTO `pk_err_code_chk` VALUES ('CI9901', 'มีรายการค่ารักษา(OPD)ยังไม่ถูกโอนเป็นผู้ป่วยใน', 'select  ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.regdate as vstdate, ipt.regtime as vsttime, ipt.an , ipt.vn  \r\n,(select  sum(sum_price) from opitemrece where vn = ipt.vn and qty > 0 group by vn) as price\r\n,(select  group_concat(distinct income.name) from opitemrece \r\n	INNER JOIN income on opitemrece.income = income.income \r\n   where vn = ipt.vn  and qty > 0 group by vn) as income\r\nfrom ipt\r\nleft join patient on  ipt.hn = patient.hn\r\nwhere ipt.dchdate is null and  (select  sum(sum_price) from opitemrece where vn = ipt.vn and  qty > 0 group by vn) > 0\r\nand concat(RIGHT(year(ipt.regdate)+543,2),LPAD(month(ipt.regdate),2,\'0\')) ', null);
INSERT INTO `pk_err_code_chk` VALUES ('CI9902', 'มีค่ารักษาโอนจาก OPD>>IPD ผิดปกติ', 'select  ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate, ovst.vsttime , ovst.an , ovst.vn , \'\' price ,\'\' income\r\nfrom ovst \r\nINNER JOIN (select ovv.vn,ovv.vstdate ovtts, (SELECT  min(vstdate) vtts\r\nFROM opitemrece \r\nwhere an = ovv.an\r\nGROUP BY an ) vtts\r\nfrom ovst ovv\r\nwhere  ovv.an is not null  \r\nHAVING vtts < ovtts ) sel_chk on ovst.vn = sel_chk.vn\r\nLEFT JOIN patient on ovst.hn = patient.hn\r\nwhere ovst.vn ', '2019-05-30 20:22:51');
