INSERT INTO `pk_err_code_chk` VALUES ('CR9901', 'ข้อมูลค่ารักษาเกินจริง(เกิน 20,000 บาท)', 'select  patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,vn_stat.vstdate ,vn_stat.income,(SELECT vsttime from ovst where vn = vn_stat.vn) as vsttime \r\n  from vn_stat \r\n  left join  patient on vn_stat.hn = patient.hn \r\n  where income > 20000 ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('CR9902', 'ข้อมูลค่าใช้จ่ายไม่ครบควรตรวจสอบ(ต้องชำระเงิน)', 'select  patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,vn_stat.vstdate ,(SELECT vsttime from ovst where vn = vn_stat.vn) as vsttime,\r\n		((paid_money - rcpt_money) - ifnull((select rcpt_arrear.amount from rcpt_arrear where vn = vn_stat.vn and paid = \'N\'),0)) as income\r\n		from vn_stat \r\n    left join  patient on vn_stat.hn = patient.hn \r\n    where  paid_money > 0 and paid_money <> rcpt_money \r\nand (if((paid_money - rcpt_money)>0 , \r\n             if(((paid_money - rcpt_money) - ifnull((select sum(rcpt_arrear.amount) from rcpt_arrear where vn = vn_stat.vn and paid = \'N\' GROUP BY vn),0)) > 0  ,\r\n							(((paid_money - rcpt_money) - ifnull((select sum(rcpt_arrear.amount) from rcpt_arrear where vn = vn_stat.vn and paid = \'N\' GROUP BY vn),0)) - ifnull((select sum(rcpt_arrear.amount) from rcpt_arrear where vn = vn_stat.vn GROUP BY vn ),0)),0)\r\n		,0 )) <> 0 and vn  ', '2017-04-19 16:39:22');
INSERT INTO `pk_err_code_chk` VALUES ('CR9903', 'ข้อมูลการจ่ายค้างชำระ แล้วเวลาจ่ายน้อยกว่าเวลา Visit(ติดลบ -)', ' select  patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname,\r\n     rcpt_arrear.receive_money_date as vstdate,rcpt_arrear.receive_money_time  as vsttime ,\r\n    (SELECT vsttime from ovst where vn = (SELECT max(vn) from ovst where hn=rcpt_arrear.hn and vstdate <= rcpt_arrear.receive_money_date  and vsttime < rcpt_arrear.receive_money_time))  as vsttime_max_ovst , \r\n    (SELECT vstdate from ovst where vn = (SELECT max(vn) from ovst where hn=rcpt_arrear.hn and vstdate <= rcpt_arrear.receive_money_date  and vsttime < rcpt_arrear.receive_money_time))  as vstdate_max_ovst ,\r\n		 rcpt_arrear.amount as income\r\n     from rcpt_arrear\r\n    left join  patient on rcpt_arrear.hn = patient.hn \r\n    where paid = \'Y\' and LENGTH(rcpt_arrear.vn) > 9 \r\n    and DATEDIFF(concat(rcpt_arrear.receive_money_date,\' \',rcpt_arrear.receive_money_time), ((SELECT concat(vstdate,\' \',vsttime) from ovst where vn = (SELECT max(vn) from ovst where hn=rcpt_arrear.hn and vstdate <= rcpt_arrear.receive_money_date  and vsttime < rcpt_arrear.receive_money_time))) ) <> 0\r\n    and concat(RIGHT(year(rcpt_arrear.receive_money_date)+543,2),LPAD(month(rcpt_arrear.receive_money_date),2,0))   ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('CR9904', 'ข้อมูลการจ่ายค้างชำระ แล้วชื่อผู้รับเงินกับชื่อผู้ออกบิลไม่ตรง(อาจติดลบ -)', ' select  patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname,\r\n     rcpt_arrear.receive_money_date as vstdate,rcpt_arrear.receive_money_time  as vsttime ,\r\n    (SELECT vsttime from ovst where vn = (SELECT max(vn) from ovst where hn=rcpt_arrear.hn and vstdate <= rcpt_arrear.receive_money_date  and vsttime < rcpt_arrear.receive_money_time))  as vsttime_max_ovst , \r\n    (SELECT vstdate from ovst where vn = (SELECT max(vn) from ovst where hn=rcpt_arrear.hn and vstdate <= rcpt_arrear.receive_money_date  and vsttime < rcpt_arrear.receive_money_time))  as vstdate_max_ovst ,\r\n		 rcpt_arrear.amount as income\r\n     from rcpt_arrear\r\n    left join  patient on rcpt_arrear.hn = patient.hn \r\n		left join rcpt_print on  rcpt_arrear.finance_number = rcpt_print.finance_number\r\n		where paid = \'Y\'  and (receive_money_staff <> rcpt_print.user)\r\n		and  concat(RIGHT(year(rcpt_print.bill_date_time)+543,2),LPAD(month(rcpt_print.bill_date_time),2,\'0\')) ', '2018-07-12 09:48:58');
