INSERT INTO `pk_err_code_chk` VALUES ('DE9901', 'มีข้อมูล (IPD) DCHTYPE เป็นตาย(08 - 09) แต่ไม่มีลงทะเบียนการตาย', 'SELECT \r\n   ipt.hn,ipt.regdate as vstdate,ipt.regtime as vsttime ,ipt.dchdate,ipt.dchtime,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.an\r\n   FROM \r\n			ipt \r\n		LEFT JOIN patient ON ipt.hn = patient.hn \r\n		LEFT JOIN death ON patient.hn = death.hn \r\n		WHERE\r\n			(\r\n				ipt.dchstts IN (\'08\', \'09\')\r\n				OR ipt.dchtype IN (\'08\', \'09\')\r\n			) and death.death_id is null ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('DE9902', 'มีข้อมูล (IPD) DCHTYPE เป็นตาย(08 - 09) แต่ไม่มีเลข AN', 'SELECT \r\n   ipt.hn,ipt.regdate as vstdate,ipt.regtime as vsttime ,ipt.dchdate,ipt.dchtime,concat(pname,\' \',fname,\' \',lname) as ptname ,death.an\r\n  FROM \r\n			ipt \r\n		LEFT JOIN patient ON ipt.hn = patient.hn \r\n		LEFT JOIN death ON patient.hn = death.hn \r\n		WHERE\r\n			(\r\n				ipt.dchstts IN (\'08\', \'09\')\r\n				OR ipt.dchtype IN (\'08\', \'09\')\r\n			) and death.death_id is not null  and death.an in(\'\',null) ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('DE9903', 'ตายในสถานพยาบาล PDEATH=1 แต่ไม่ระบุโรงพยาบาลที่ตาย HOSPDEATH', 'SELECT \r\n   patient.hn,death.death_date as vstdate ,  death.death_time as vsttime ,concat(pname,\' \',fname,\' \',lname) as ptname ,death.an\r\n   FROM \r\n			death \r\n		LEFT JOIN patient ON death.hn = patient.hn     \r\n		WHERE  death_place = 1  and  (death_hospcode = \'\' or  death_hospcode is null)\r\n     and concat(RIGHT((DATE_FORMAT(death.last_update ,\'%Y\') +543),2),DATE_FORMAT(death.last_update ,\'%m\')) ', '2019-04-09 14:06:14');
INSERT INTO `pk_err_code_chk` VALUES ('DO1101', 'ไม่มีรหัสยา 24 หลัก เป็นค่าว่าง(null)', 'select  opitemrece.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,opitemrece.vstdate as vstdate , (select vsttime from ovst where vn = opitemrece.vn) as vsttime , drugitems.generic_name , drugitems.icode\r\n  from opitemrece \r\nINNER  join drugitems on opitemrece.icode = drugitems.icode\r\nleft join patient on  opitemrece.hn = patient.hn \r\nwhere drugitems.did is NULL', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('DO9901', 'มีข้อมูลหัตถการฉีดวัคซีนพิษสุนัขบ้า แต่ไม่มีรายการวัคซีน Rabies Vaccine', '\r\nselect  ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate , ovst.vsttime as vsttime , drugitems.generic_name , drugitems.icode\r\n  from er_regist_oper \r\n  left join ovst_vaccine on er_regist_oper.vn = ovst_vaccine.vn and ovst_vaccine.person_vaccine_id in(\'36\',\'37\',\'38\',\'39\',\'40\')  \r\n	left join ovst on er_regist_oper.vn = ovst.vn \r\n	left join patient on ovst.hn = patient.hn \r\n  left join opdscreen on er_regist_oper.vn = opdscreen.vn \r\n  left join opitemrece on er_regist_oper.vn = opitemrece.vn and opitemrece.icode = \'1900423\' \r\n  left join drugitems on opitemrece.icode = drugitems.icode \r\n   where er_oper_code =\'178\' and opitemrece.vn is null ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('DO9902', 'มีข้อมูลหัตถการฉีดวัคซีนบาดทะยัก แต่ไม่มีรายการวัคซีน TETANUS VACCINE', 'select  ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate,ovst.vsttime as vsttime , drugitems.generic_name , drugitems.icode\r\n  from er_regist_oper \r\n  left join ovst_vaccine on er_regist_oper.vn = ovst_vaccine.vn and ovst_vaccine.person_vaccine_id in(\'31\',\'32\',\'33\',\'34\',\'35\')  \r\n	left join ovst on er_regist_oper.vn = ovst.vn \r\n	left join patient on ovst.hn = patient.hn \r\n  left join opdscreen on er_regist_oper.vn = opdscreen.vn \r\n  left join opitemrece on er_regist_oper.vn = opitemrece.vn and opitemrece.icode in(\'1900485\',\'1480175\')\r\n  left join drugitems on opitemrece.icode = drugitems.icode \r\n   where er_oper_code =\'176\' and opitemrece.vn is null  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('DO9903', 'มีข้อมูลหัตถการ พ่นยา แต่ไม่มีรายการยา (Ventolin Solution ,Beraduol Solution,NSS 0.9%,Adrenaline )', '\r\nselect  ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate,ovst.vsttime as vsttime , \'\' as generic_name , \'\' as icode\r\n  from er_regist_oper \r\n	left join ovst on er_regist_oper.vn = ovst.vn \r\n	left join patient on ovst.hn = patient.hn \r\n  left join opdscreen on er_regist_oper.vn = opdscreen.vn \r\n   where er_oper_code =\'22\' and (select vn from opitemrece where vn = er_regist_oper.vn and opitemrece.icode in(\'1900435\',\'1540004\',\'1550009\',\'1000010\') limit 1 ) is null\r\n		and ovst.an is null  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('DO9904', 'มีข้อมูลหัตถการฉีดยา แต่ไม่มีรายการยา(ชนิดฉีด)', 'select  ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate,ovst.vsttime as vsttime , \'\' as generic_name , \'\' as icode\r\n   FROM er_regist_oper \r\n   left join opitemrece on er_regist_oper.vn = opitemrece.vn  and opitemrece.icode in(select icode from drugitems where dosageform like \'%INJ%\')\r\n   left join ovst on er_regist_oper.vn = ovst.vn  \r\n   left join patient on ovst.hn = patient.hn \r\n    where  er_regist_oper.er_oper_code in(select er_oper_code from er_oper_code where NAME like\'%ฉีด%\') and ovst.an is null and opitemrece.vn is null \r\n				   and er_regist_oper.vn not in(SELECT vn from opdscreen where ((cc like\'%stre%\' or cc like\'%ste%\' or cc like\'%hype%\' or cc like\'%levo%\' or cc like\'%มาเอง%\') \r\n						or (hpi like\'%stre%\' or hpi like\'%ste%\' or hpi like\'%hype%\' or hpi like\'%levo%\'))  and opdscreen.vn = er_regist_oper.vn )  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('DS9901', 'รหัส ICD10 ผู้พิการไม่ตรงตามมาตรฐาน', 'select person.person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y  ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)  \r\n end  as hous_addr  ,ps_drforemd.icd10 ,ps_drforemd.register_date ,date(ps_drforemd.entry_datetime) as entry_date \r\n  from person\r\n  INNER JOIN (SELECT person_deformed.person_id ,person_deformed.register_date, person_deformed_detail.entry_datetime , person_deformed_detail.icd10 \r\n  from person_deformed_detail\r\n  LEFT JOIN person_deformed ON  person_deformed_detail.person_deformed_id=  person_deformed.person_deformed_id\r\n  where ASCII(icd10) not BETWEEN \'65\' and \'90\') ps_drforemd on person.person_id = ps_drforemd.person_id  ', '2016-11-21 16:23:46');
