INSERT INTO `pk_err_code_chk` VALUES ('DX1130', 'ข้อมูลรหัสการวินิจฉัย(DIAGCODE) เป็นค่าว่าง (null)', 'select  patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,vn_stat.vstdate ,if(ovst.an is null , null,\'Admit\') as dxtype\r\n,ovst.vsttime    from vn_stat  \r\nleft join patient on   vn_stat.hn = patient.hn\r\nleft join ovst on vn_stat.vn = ovst.vn\r\nwhere (vn_stat.pdx is null  or vn_stat.pdx = \'\') and vn_stat.vn ', '2016-07-08 11:09:22');
