INSERT INTO `pk_err_code_chk` VALUES ('DX9902', 'รหัส Diag Type ไม่ใช่(External Cause) V01-Y98', 'select  ovstdiag.vn,patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,ovstdiag.vstdate , concat(ovstdiag.icd10,\' :\',ovstdiag.diagtype) as dxtype\r\n,ovstdiag.vsttime   \r\n from ovstdiag  \r\nleft join patient on   ovstdiag.hn = patient.hn\r\nINNER JOIN (SELECT `code` FROM icd101 where code BETWEEN \'V01%\' and \'Y98%\') ext_diag on ovstdiag.icd10 = ext_diag.`code`\r\nwhere ovstdiag.diagtype <> 5 and ovstdiag.vn  ', '2017-03-18 00:27:38');
INSERT INTO `pk_err_code_chk` VALUES ('DX9903', 'มีรหัส S,T แต่ไม่มีรหัส W,X,Y ร่วม', 'select  ovstdiag.vn,patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,ovstdiag.vstdate , concat(ovstdiag.icd10,\' :\',ovstdiag.diagtype) as dxtype\r\n,ovstdiag.vsttime  \r\nfrom ovstdiag \r\nleft join patient on   ovstdiag.hn = patient.hn\r\nwhere (icd10 like\'S%\' or icd10 like\'T%\') and  (SELECT DISTINCT(vdx1.vn) from ovstdiag  vdx1 where ( vdx1.icd10 like\'V%\' or  vdx1.icd10 like\'W%\' or vdx1.icd10 like\'X%\' or vdx1.icd10 like\'Y%\') and vdx1.vn = ovstdiag.vn) is null\r\nand vn  ', '2017-08-03 10:49:11');
INSERT INTO `pk_err_code_chk` VALUES ('DX9904', 'รหัสร่วม(type 5)  ต้องเป็น \'V\',\'W\',\'X\',\'Y\' เท่านั้น', 'select  ovstdiag.vn,patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,ovstdiag.vstdate , concat(ovstdiag.icd10,\' :\',ovstdiag.diagtype) as dxtype\r\n,ovstdiag.vsttime \r\nfrom ovstdiag \r\ninner join (\r\nselect vn from ovstdiag   where  left(icd10,1) not in (\'V\',\'W\',\'X\',\'Y\') and diagtype = 5\r\nunion\r\nselect vn from ovstdiag   where  left(icd10,1)  in (\'V\',\'W\',\'X\',\'Y\') and diagtype != 5\r\n) diag_error on ovstdiag.vn = diag_error.vn \r\nleft join patient on   ovstdiag.hn = patient.hn\r\nwhere ovstdiag.vn ', '2018-04-03 15:57:11');
INSERT INTO `pk_err_code_chk` VALUES ('DX9905', 'ห้ามใช้รหัส T310-T319 เป็น PDX', 'select  ovstdiag.vn,patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,ovstdiag.vstdate , concat(ovstdiag.icd10,\' :\',ovstdiag.diagtype) as dxtype\r\n,ovstdiag.vsttime \r\nfrom ovstdiag \r\nINNER JOIN (SELECT `code` FROM icd101 where code BETWEEN \'T310\' and \'T319\') ext_diag on ovstdiag.icd10 = ext_diag.`code`\r\nleft join patient on   ovstdiag.hn = patient.hn\r\nwhere ovstdiag.vn ', '2018-04-04 09:44:14');
INSERT INTO `pk_err_code_chk` VALUES ('DX9906', 'การให้รหัส Z470-Z479 และ Z480-Z489 ต้องไม่ใช้ร่วมกับรหัสกลุ่ม S หรือ T', 'SELECT\r\n	ovstdiag.vn,\r\n	patient.hn,\r\n	patient.cid,\r\n	concat(\r\n		patient.pname,\r\n		\' \',\r\n		patient.fname,\r\n		\' \',\r\n		patient.lname\r\n	) AS ptname,\r\n	ovstdiag.vstdate,\r\n	concat(\r\n		ovstdiag.icd10,\r\n		\' :\',\r\n		ovstdiag.diagtype\r\n	) AS dxtype,\r\n	ovstdiag.vsttime\r\n\r\nFROM	ovstdiag\r\nLEFT JOIN patient ON ovstdiag.hn = patient.hn\r\nWHERE(icd10 BETWEEN \'Z480\' AND \'Z489\' or icd10 BETWEEN \'Z470\' AND \'Z479\'	)\r\n AND (SELECT DISTINCT	(vdx1.vn)	FROM	ovstdiag vdx1	WHERE (	vdx1.icd10 BETWEEN \'S%\' AND \'T%\')	AND vdx1.vn = ovstdiag.vn\r\n) IS NOT  NULL AND vn ', '2018-04-04 10:25:48');
INSERT INTO `pk_err_code_chk` VALUES ('DX9910', 'มีรายการ LAB-SARS แต่ Diag ไม่มีรหัสตามที่กำหนด', 'SELECT\r\n	ovstdiag.vn,\r\n	patient.hn,\r\n	patient.cid,\r\n	concat(\r\n		patient.pname,\r\n		\' \',\r\n		patient.fname,\r\n		\' \',\r\n		patient.lname\r\n	) AS ptname,\r\n	ovstdiag.vstdate,\r\n	group_concat(concat(\r\n		ovstdiag.icd10,\r\n		\' :\',\r\n		ovstdiag.diagtype\r\n	)) AS dxtype,\r\n	ovstdiag.vsttime\r\n\r\nfrom ovstdiag \r\n LEFT JOIN patient ON ovstdiag.hn = patient.hn\r\n LEFT JOIN ovst on ovstdiag.vn = ovst.vn\r\n INNER JOIN (SELECT lab_head.vn\r\n    from lab_order \r\n left join lab_head on lab_order.lab_order_number = lab_head.lab_order_number \r\n LEFT JOIN ovst ov1 on lab_head.vn  = ov1.vn \r\nwhere lab_order.lab_items_code = \'830\' and  if(lab_order.lab_order_result = \'Positive\' , \r\n          if((SELECT count(icd10) from ovstdiag where ovstdiag.vn = ov1.vn and ovstdiag.icd10 in(\'U071\',\'U072\',\'Z290\')> 0)  ,\'Ok\',\'NG\'),\r\n          if((SELECT count(icd10) from ovstdiag where ovstdiag.vn = ov1.vn and ovstdiag.icd10 in(\'Z115\')> 0),\'Ok\',\'NG\')) = \'NG\')  chk on ovstdiag.vn = chk.vn\r\n\r\nwhere  ASCII(SUBSTR(icd10,1,1)) BETWEEN 65 and 90 and  ovst.vn ', '2022-04-11 10:43:01');
INSERT INTO `pk_err_code_chk` VALUES ('DX9911', 'มีการให้รหัส U เป็น PDX', 'SELECT\r\n	ovstdiag.vn,\r\n	patient.hn,\r\n	patient.cid,\r\n	concat(\r\n		patient.pname,\r\n		\' \',\r\n		patient.fname,\r\n		\' \',\r\n		patient.lname\r\n	) AS ptname,\r\n	ovstdiag.vstdate,\r\n	group_concat(concat(\r\n		ovstdiag.icd10,\r\n		\' :\',\r\n		ovstdiag.diagtype\r\n	)) AS dxtype,\r\n	ovstdiag.vsttime\r\n\r\nfrom ovstdiag \r\n LEFT JOIN patient ON ovstdiag.hn = patient.hn\r\n LEFT JOIN ovst on ovstdiag.vn = ovst.vn\r\n where  ASCII(SUBSTR(icd10,1,1)) = 85 and ovstdiag.diagtype = 1 and  ovstdiag.icd10 not in(\'U119\') and ovst.vn  ', '2022-04-21 16:32:30');
INSERT INTO `pk_err_code_chk` VALUES ('DX9912', 'จ่ายยา(มะขามป้อม)แต่ไม่มี U643,U5831,U5931', 'SELECT\r\n	ovst.vn,\r\n	patient.hn,\r\n	patient.cid,\r\n	concat(\r\n		patient.pname,\r\n		\' \',\r\n		patient.fname,\r\n		\' \',\r\n		patient.lname\r\n	) AS ptname,\r\n	\"\" AS dxtype,\r\n	ovst.vstdate\r\n\r\n                from ovst \r\n                 LEFT JOIN patient ON ovst.hn = patient.hn\r\n                 LEFT JOIN pttype on  ovst.pttype = pttype.pttype \r\n                where  ovst.vn in(\r\n                    SELECT v1.vn\r\n                    from ovst v1\r\n                    INNER JOIN opitemrece on v1.vn =  opitemrece.vn\r\n                    left JOIN ovstdiag on v1.vn = ovstdiag.vn \r\n                    where opitemrece.icode in(\'1660003\') and v1.an is null\r\n                    GROUP BY v1.vn \r\n                    HAVING (group_concat(DISTINCT(case when ovstdiag.icd10 in(\'U643\',\'U5831\',\'U5931\') then \'OK\' else \'CHK\' end )) like\'CHK\')\r\n                    ) and ovst.an is null and pttype.hipdata_code in(\'UCS\') and pttype.isuse = \'Y\' and  ovst.vn ', '2023-04-26 11:19:11');
