INSERT INTO `pk_err_code_chk` VALUES ('EP9901', 'ลงข้อมูลการฉีดวัคซีนก่อนวันที่เกิด', 'SELECT \'WBC\' as \'Items\',concat(person.pname ,\'\',person.fname,\' \',person.lname) as ptname,person.cid,person_wbc.person_id as pid ,person.birthdate , person_wbc_service.service_date as date_service ,wbc_vaccine.wbc_vaccine_code as vcc_code\r\nfrom person_wbc \r\nLEFT JOIN person_wbc_service on person_wbc.person_wbc_id = person_wbc_service.person_wbc_id\r\nleft join person on person_wbc.person_id = person.person_id \r\nleft JOIN person_wbc_vaccine_detail on person_wbc_service.person_wbc_service_id = person_wbc_vaccine_detail.person_wbc_service_id\r\nLEFT JOIN wbc_vaccine on person_wbc_vaccine_detail.wbc_vaccine_id = wbc_vaccine.wbc_vaccine_id\r\nwhere person.birthdate >  person_wbc_service.service_date\r\nUNION\r\nSELECT \'EPI\' as \'Items\',concat(person.pname ,\'\',person.fname,\' \',person.lname) as ptname,person.cid,person_epi.person_id  as pid ,person.birthdate ,person_epi_vaccine.vaccine_date as date_service  , epi_vaccine.vaccine_code as vcc_code\r\nfrom person_epi \r\nLEFT JOIN person_epi_vaccine on person_epi.person_epi_id = person_epi_vaccine.person_epi_id\r\nleft join person on person_epi.person_id = person.person_id \r\nLEFT JOIN person_epi_vaccine_list on person_epi_vaccine.person_epi_vaccine_id = person_epi_vaccine_list.person_epi_vaccine_id\r\nLEFT JOIN epi_vaccine on person_epi_vaccine_list.epi_vaccine_id = epi_vaccine.epi_vaccine_id\r\nwhere person.birthdate >  person_epi_vaccine.vaccine_date\r\nUNION\r\nSELECT \'ElseWhere\' as \'Items\',concat(person.pname ,\'\',person.fname,\' \',person.lname) as ptname,person.cid,person_vaccine_elsewhere.person_id  as pid ,person.birthdate ,person_vaccine_elsewhere.vaccine_date as date_service  , person_vaccine.vaccine_code as vcc_code\r\nfrom person_vaccine_elsewhere\r\nleft JOIN person on person_vaccine_elsewhere.person_id = person.person_id\r\nleft JOIN person_vaccine on person_vaccine_elsewhere.person_vaccine_id = person_vaccine.person_vaccine_id\r\nwhere person.birthdate > person_vaccine_elsewhere.vaccine_date\r\nGROUP BY pid,vcc_code ORDER BY birthdate   ', '2016-11-24 13:27:24');
INSERT INTO `pk_err_code_chk` VALUES ('EP9902', 'การลง Vaccine (เด็กนักเรียน บช.5) ซ้ำซ้อน', 'SELECT \'EPI\' as \'Items\' ,concat(patient.pname ,\'\',patient.fname,\' \',patient.lname) as ptname,patient.cid,student_vaccine_chk.person_id as pid ,patient.birthday as \'birthdate\' ,ovst.vstdate as date_service \r\n , student_vaccine_chk.vaccine_name as vcc_code \r\nfrom ovst\r\ninner join (\r\nselect village_student_vaccine.vn ,  ovst_vaccine.vn as vn_chk ,person_vaccine.vaccine_name ,village_student.person_id \r\nfrom village_student_vaccine\r\nleft join village_student on village_student_vaccine.village_student_id =  village_student.village_student_id\r\nleft join village_student_vaccine_list on village_student_vaccine.village_student_vaccine_id = village_student_vaccine_list.village_student_vaccine_id\r\nLEFT JOIN student_vaccine on village_student_vaccine_list.student_vaccine_id  = student_vaccine.student_vaccine_id\r\nINNER JOIN ovst_vaccine on village_student_vaccine.vn  = ovst_vaccine.vn\r\nleft join person_vaccine on ovst_vaccine.person_vaccine_id = person_vaccine.person_vaccine_id\r\nwhere  student_vaccine.export_vaccine_code = person_vaccine.export_vaccine_code\r\nHAVING vn_chk is not null\r\n) student_vaccine_chk on student_vaccine_chk.vn =  ovst.vn\r\nLEFT JOIN patient on ovst.hn = patient.hn \r\nwhere ovst.vn ', '2017-11-02 15:52:36');
