INSERT INTO `pk_err_code_chk` VALUES ('FN1106', 'ไม่มีข้อมูล คะแนนความบกพร่อง (TESTRESULT)   ', 'SELECT concat(patient.pname,patient.fname,\" \",patient.lname)as \"ptname\" , patient.cid ,patient.hn,ovst.vstdate , ovst.vsttime\r\nfrom ovst\r\nINNER JOIN (SELECT vn \r\nFROM ovst_functional \r\nwhere (ovst_functional_score is null)) function_chk on ovst.vn = function_chk.vn\r\nleft join patient on ovst.hn = patient.hn\r\nwhere ovst.vn ', '2018-04-23 16:30:21');
INSERT INTO `pk_err_code_chk` VALUES ('FN2107', 'ข้อมูล ภาวะพึ่งพิงของผู้สูงอายุ (DEPENDENT) ค่าไม่ตรงกับมาตรฐาน', 'SELECT concat(patient.pname,patient.fname,\" \",patient.lname)as \"ptname\" , patient.cid ,patient.hn,ovst.vstdate , ovst.vsttime\r\nfrom ovst\r\nINNER JOIN (SELECT vn \r\nFROM ovst_functional \r\nwhere (ovst_functional_dependent_type_id is null or ovst_functional_dependent_type_id = \'\')) function_chk on ovst.vn = function_chk.vn\r\nleft join patient on ovst.hn = patient.hn\r\nwhere ovst.vn ', '2018-04-23 16:30:27');
INSERT INTO `pk_err_code_chk` VALUES ('FN2108', ' ภาวะพึ่งพิงของผู้สูงอายุ (DEPENDENT) ไม่สัมพันธ์กับคะแนนความบกพร่อง (TESTRESULT)', 'SELECT concat(patient.pname,patient.fname,\" \",patient.lname)as \"ptname\" , patient.cid ,patient.hn,ovst.vstdate , ovst.vsttime\r\nfrom ovst\r\nINNER JOIN (SELECT vn \r\n					FROM ovst_functional \r\n					where (ovst_functional_score is not null) and ovst_functional_score BETWEEN 0 and 4 and ovst_functional_dependent_type_id <> 3\r\n					UNION\r\n					SELECT vn \r\n					FROM ovst_functional \r\n					where (ovst_functional_score is not null) and ovst_functional_score BETWEEN 5 and 11 and ovst_functional_dependent_type_id <> 2\r\n					UNION\r\n					SELECT vn \r\n					FROM ovst_functional \r\n					where (ovst_functional_score is not null) and ovst_functional_score BETWEEN  12 and 20 and ovst_functional_dependent_type_id <> 1\r\n\r\n						) function_chk on ovst.vn = function_chk.vn\r\nleft join patient on ovst.hn = patient.hn\r\nwhere ovst.vn  ', '2018-04-23 16:30:30');
INSERT INTO `pk_err_code_chk` VALUES ('FN2109', ' กาาลงผลวินิจฉัย(DIAG) ไม่สัมพันธ์กับภาวะพึ่งพิงของผู้สูงอายุ (DEPENDENT)', 'SELECT concat(patient.pname,patient.fname,\" \",patient.lname)as \"ptname\" , patient.cid ,patient.hn,ovst.vstdate , ovst.vsttime\r\nfrom ovst\r\nINNER JOIN (SELECT vn \r\n						FROM ovst_functional \r\n						where ovst_functional_dependent_type_id = 3 \r\n							and (SELECT vn from ovstdiag where icd10 =\'Z508\' and vn = ovst_functional.vn) is null\r\n\r\n						UNION\r\n						SELECT vn \r\n						FROM ovst_functional \r\n						where ovst_functional_dependent_type_id = 2 \r\n							 and (SELECT vn from ovstdiag where icd10 =\'Z749\' and vn = ovst_functional.vn) is null\r\n\r\n						UNION\r\n						SELECT vn \r\n						FROM ovst_functional \r\n						where ovst_functional_dependent_type_id = 1 \r\n							 and (SELECT vn from ovstdiag where icd10 =\'Z718\' and vn = ovst_functional.vn) is null\r\n) function_chk on ovst.vn = function_chk.vn\r\nleft join patient on ovst.hn = patient.hn\r\nwhere ovst.vn  ', '2018-04-23 16:33:56');
