INSERT INTO `pk_err_code_chk` VALUES ('FP9241', 'ข้อมูลรหัสวิธีการคุมกำเนิด(FPTYPE) มีค่าไม่ใช่ 1-9', 'select  hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person_women_service.service_date as vstdate ,person_women_service.person_women_service_id\r\n   from person_women_service \r\nleft JOIN person_women  on person_women_service.person_women_id = person_women.person_women_id\r\nleft join patient on (select cid from person where person_id = person_women.person_id) = patient.cid\r\nwhere person_women_service.women_service_id = 1 and  person_women.women_birth_control_id not in(select women_birth_control_id from women_birth_control)\r\n', '2016-07-08 11:09:22');
