INSERT INTO `pk_err_code_chk` VALUES ('HO2104 ', 'ข้อมูล ประเภทที่อยู่ (HOUSETYPE) ค่าไม่ตรงกับมาตรฐาน', '\r\nSELECT house.address , village.village_moo, village.village_name , house.census_id,house.doctor_code,house.house_id\r\n  from house\r\n  left join village on house.village_id = village.village_id\r\n  where (house_subtype_id not in(SELECT house_subtype_id from house_subtype) or house_subtype_id is null)  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('HO9901', 'ข้อมูล บ้านที่ไม่มีเจ้าบ้าน', '\r\nSELECT house.house_id,house.address , village.village_moo, village.village_name , house.census_id,house.doctor_code ,ps_house_id.person_id\r\n  from house\r\n  left join village on (house.village_id) = village.village_id\r\n  left join (SELECT house_id,person_id from person where person_house_position_id = 1) ps_house_id on house.house_id=ps_house_id.house_id\r\n  where   house.village_id <> 1 and ps_house_id.person_id is null order by village.village_id , house.address  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('HO9902', 'ข้อมูลบ้านที่มี อสม. มากกว่า 1 คน', 'SELECT house.house_id,house.address , village.village_moo, village.village_name , house.census_id,house.doctor_code ,\r\n\'\' person_id ,hous_chk.vms_mid as vrms_mid\r\n  from house\r\n  left join village on (house.village_id) = village.village_id\r\n  INNER JOIN (SELECT vms.house_id  ,GROUP_CONCAT(village_organization_mid) vms_mid\r\n						from village_organization_member_service vms                                \r\n			      left outer join village v on v.village_id=vms.village_id                    \r\n			      left outer join house h on h.house_id=vms.house_id                          \r\n			      where vms.village_id <> 1                             \r\n			      group by vms.house_id                                                       \r\n			      having count(*) > 1 order by vms.village_id) hous_chk on house.house_id = hous_chk.house_id\r\n   order by village.village_id , house.address  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('HO9903', 'ข้อมูลบ้านที่ไมมี อสม. ประจำบ้าน', 'SELECT house.house_id,house.address , village.village_moo, village.village_name , house.census_id,house.doctor_code ,\'\' person_id\r\n  from house\r\n  left join village on (house.village_id) = village.village_id\r\n  LEFT JOIN (SELECT house_id from village_organization_member_service group by house_id) vms on house.house_id = vms.house_id\r\n where vms.house_id is null and house.village_id <> 1   \r\n order by village.village_id , house.address   ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('HO9904', 'ข้อมูลบ้านที่ไม่มีคนมีชีวิต อาศัยอยู่', 'SELECT house.house_id,house.address , village.village_moo, village.village_name , house.census_id,house.doctor_code ,vms.person_id ,vms.pt_name\r\n  from house\r\n  left join village on (house.village_id) = village.village_id\r\n  LEFT JOIN (SELECT  person.house_id,village_moo,GROUP_CONCAT(DISTINCT(house.address)) as address, count(person.person_id) as cc , sum(if(death=\'Y\',1,0)) as c_death  , \r\n (count(person_id) - sum(if(death=\'Y\',1,0))) as chk , GROUP_CONCAT(DISTINCT(person.person_id)) as person_id ,     GROUP_CONCAT(concat(person.pname,person.fname,\' \',person.lname)) as pt_name\r\nfrom person \r\nLEFT JOIN house on person.house_id = house.house_id\r\nLEFT JOIN village on house.village_id = village.village_id\r\nGROUP BY person.house_id\r\nHAVING chk = 0 ) vms on house.house_id = vms.house_id\r\n where house.village_id <> 1 and  vms.house_id is not null \r\n order by village.village_id , house.address    ', '2022-07-05 18:32:57');
