INSERT INTO `pk_err_code_chk` VALUES ('LB1105', 'ข้อมูลวันแรกของการมีประจำเดือนครั้งสุดท้าย (LMP) ไม่อยู่ในรูปของ YYYYMMDD หรือ YYYY ไม่เป็น ค.ศ. หรือเป็นค่าว่าง (null)', '\r\nselect  ipt.an,ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.regdate as vstdate ,ipt.regtime as vsttime , ipt_labour.g  as preg_no\r\n from ipt_labour  \r\n left join ipt on ipt_labour.an = ipt.an  \r\n left join patient on  ipt.hn = patient.hn  \r\n where  (ipt_labour.lmp is null or ipt_labour.lmp = \'\')   and ipt.an in (select an from labor )   and ipt.ipt_type = 4   \r\n ', '2016-07-08 11:09:22');
