INSERT INTO `pk_err_code_chk` VALUES ('LB9904', 'จำนวนบุตร กับ ข้อมูลทารกไม่เท่ากัน', 'select  ipt.an,ipt.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ipt.regdate as vstdate ,ipt.regtime as vsttime , ipt_pregnancy.preg_number  as preg_no\r\nfrom ipt_pregnancy \r\nleft JOIN (SELECT mother_an as an,count(an) as CC , an as child_an \r\nfrom ipt_newborn\r\nGROUP BY mother_an) chilcount on ipt_pregnancy.an = chilcount.an\r\nleft join ipt on ipt_pregnancy.an = ipt.an\r\nleft join patient on  ipt.hn = patient.hn \r\nwhere ipt_pregnancy.child_count <> chilcount.CC ', '2017-03-27 11:08:15');
