INSERT INTO `pk_err_code_chk` VALUES ('NE1105', 'ข้อมูลวันเดือนปีที่ลูกได้รับการดูแล (BCARE)ไม่อยู่ในรูปของ YYYYMMDD หรือเป็นค่าว่าง(null)', '\r\nselect  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,care_date as vstdate ,\r\nperson_wbc_post_care.care_number,DATEDIFF(person_wbc_post_care.care_date,person.birthdate) as day_cc ,DATEDIFF(CURDATE(),person.birthdate) as day_labor	\r\n							,\'\'  as \'@start_date\' ,   \'\'  as \'@end_date\'	\r\n     from person_wbc_post_care\r\n				left join person on (select person_id from person_wbc where  person_wbc.person_wbc_id = person_wbc_post_care.person_wbc_id limit 1) = person.person_id\r\n				where (person_wbc_post_care.care_date is null or person_wbc_post_care.care_date = \'\' )\r\n				and  CONCAT(SUBSTR(((year(care_date))+543),3,2),if(LENGTH(MONTH(care_date))=1,concat(\'0\',MONTH(care_date)),MONTH(care_date)))  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('NE1106', 'ข้อมูลผลการตรวจทารกหลังคลอด (BCARERESULT) ไม่ใช่ค่า 1,2,9 หรือเป็นค่าว่าง (null)', 'select  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,care_date as vstdate ,\r\nperson_wbc_post_care.care_number,DATEDIFF(person_wbc_post_care.care_date,person.birthdate) as day_cc ,DATEDIFF(CURDATE(),person.birthdate) as day_labor	\r\n,\'\'  as \'@start_date\' ,    \'\'  as \'@end_date\' 				\r\nfrom person_wbc_post_care\r\n				left join person on (select person_id from person_wbc where  person_wbc.person_wbc_id = person_wbc_post_care.person_wbc_id limit 1) = person.person_id\r\n				where (person_wbc_post_care.person_wbc_post_care_result_type_id is null or person_wbc_post_care.person_wbc_post_care_result_type_id = \'\' or \r\n				       person_wbc_post_care.person_wbc_post_care_result_type_id not in(select person_wbc_post_care_result_type_id from person_wbc_post_care_result_type))\r\n				and  CONCAT(SUBSTR(((year(care_date))+543),3,2),if(LENGTH(MONTH(care_date))=1,concat(\'0\',MONTH(care_date)),MONTH(care_date)))  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('NE1107', 'ข้อมูลอาหารที่รับประทาน (FOOD) ไม่ใช่ค่า 1,2,3,4 หรือ เป็นค่าว่าง(null)', 'select  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,care_date as vstdate ,\r\nperson_wbc_post_care.care_number,DATEDIFF(person_wbc_post_care.care_date,person.birthdate) as day_cc ,DATEDIFF(CURDATE(),person.birthdate) as day_labor \r\n			,\'\'  as \'@start_date\' ,    \'\'  as \'@end_date\'	\r\n	from person_wbc_post_care\r\n				left join person on (select person_id from person_wbc where  person_wbc.person_wbc_id = person_wbc_post_care.person_wbc_id limit 1) = person.person_id\r\n				where (person_wbc_post_care.person_nutrition_food_type_id is null or person_wbc_post_care.person_nutrition_food_type_id = \'\' or \r\n				       person_wbc_post_care.person_nutrition_food_type_id not in(select person_nutrition_food_type_id from person_nutrition_food_type))\r\n				and  CONCAT(SUBSTR(((year(care_date))+543),3,2),if(LENGTH(MONTH(care_date))=1,concat(\'0\',MONTH(care_date)),MONTH(care_date)))  ', '2016-07-08 11:09:22');
