INSERT INTO `pk_err_code_chk` VALUES ('NE9290', 'จำนวนวันที่เยียมกับครั้งที่เยี่ยมเด็กไม่ตรงตามกรม อานามัยกำหนด', '\r\nselect  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,care_date as vstdate ,\r\nperson_wbc_post_care.care_number,DATEDIFF(person_wbc_post_care.care_date,person.birthdate) as day_cc  ,DATEDIFF(CURDATE(),person.birthdate) as day_labor	\r\n				 ,DATE_ADD(person.birthdate, INTERVAL 1 day)  as \'@start_date\' ,    DATE_ADD(person.birthdate, INTERVAL 7 day)  as \'@end_date\'\r\n           from person_wbc_post_care\r\n						left join person on (select person_id from person_wbc where  person_wbc.person_wbc_id = person_wbc_post_care.person_wbc_id limit 1) = person.person_id\r\n						where  (person.house_id <> 1 or person.house_regist_type_id in(1,3))  \r\n				    and (case when (person_wbc_post_care.care_number = 1 and DATEDIFF(person_wbc_post_care.care_date,person.birthdate)  between 0 and 7) then \'OK\' \r\n							when (person_wbc_post_care.care_number = 2 and DATEDIFF(person_wbc_post_care.care_date,person.birthdate)  between 8 and 15) then \'OK\'  \r\n							when (person_wbc_post_care.care_number = 3 and DATEDIFF(person_wbc_post_care.care_date,person.birthdate)  between 16 and 42) then \'OK\' ELSE \r\n				\'NG\' END ) = \'NG\' and person.birthdate BETWEEN \r\n', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('NE9299', 'มีข้อมูลซ้ำซ้อน', '\r\nselect  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,care_date as vstdate ,\r\nperson_wbc_post_care.care_number,DATEDIFF(person_wbc_post_care.care_date,person.birthdate) as day_cc ,DATEDIFF(CURDATE(),person.birthdate) as day_labor\r\n				,\'\'  as \'@start_date\' , \'\'  as \'@end_date\'\r\n    from person_wbc_post_care\r\n				left join person on (select person_id from person_wbc where  person_wbc.person_wbc_id = person_wbc_post_care.person_wbc_id limit 1) = person.person_id\r\n				where  CONCAT(SUBSTR(((year(care_date))+543),3,2),if(LENGTH(MONTH(care_date))=1,concat(\'0\',MONTH(care_date)),MONTH(care_date))) ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('NE9901', 'เด็กที่ต้องเยี่ยมหลังคลอดครั้งที่ 1', 'select  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,\'\' as vstdate ,\r\n\'\' as care_number, 0 as day_cc 	,DATEDIFF(CURDATE(),person.birthdate) as day_labor ,DATE_ADD(person.birthdate, INTERVAL 1 day)  as \'@start_date\' , \r\n   DATE_ADD(person.birthdate, INTERVAL 7 day)  as \'@end_date\'\r\n	from person_wbc 	\r\n	left join person on person_wbc.person_id = person.person_id	\r\n	where person.house_regist_type_id in(1,3) and DATEDIFF(CURDATE(),person.birthdate) > 0 and baby_service1_date is null	\r\n    and DATEDIFF(person_wbc.person_wbc_regdate,person.birthdate) < 8	and  person.birthdate BETWEEN  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('NE9902', 'เด็กที่ต้องเยี่ยมหลังคลอดครั้งที่ 2', 'select  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,\'\' as vstdate ,\r\n\'\' as care_number,0 as day_cc ,DATEDIFF(CURDATE(),person.birthdate) as day_labor ,DATE_ADD(person.birthdate, INTERVAL 8 day)  as \'@start_date\' , \r\n   DATE_ADD(person.birthdate, INTERVAL 15 day)  as \'@end_date\' 		\r\n	from person_wbc 	\r\n	left join person on person_wbc.person_id = person.person_id	\r\n	where person.house_regist_type_id in(1,3) and DATEDIFF(CURDATE(),person.birthdate) > 7 and baby_service2_date is null	\r\n    and DATEDIFF(person_wbc.person_wbc_regdate,person.birthdate) < 16   and  person.birthdate BETWEEN ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('NE9903', 'เด็กที่ต้องเยี่ยมหลังคลอดครั้งที่ 3', 'select  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id  ,person.birthdate ,person.birthtime ,\'\' as vstdate ,\r\n\'\' as care_number, 0 as day_cc 	,DATEDIFF(CURDATE(),person.birthdate) as day_labor,DATE_ADD(person.birthdate, INTERVAL 16 day)  as \'@start_date\' , \r\n   DATE_ADD(person.birthdate, INTERVAL 42 day)  as \'@end_date\'\r\n	from person_wbc 	\r\n	left join person on person_wbc.person_id = person.person_id	\r\n	where person.house_regist_type_id in(1,3) and DATEDIFF(CURDATE(),person.birthdate) > 15 and baby_service3_date is null	\r\n    and DATEDIFF(person_wbc.person_wbc_regdate,person.birthdate) < 43	and  person.birthdate BETWEEN  ', '2016-07-08 11:09:22');
