INSERT INTO `pk_err_code_chk` VALUES ('NU9299', 'ข้อมูลเด็ก(บัญชี 3) ซ้ำซ้อน', 'select  cid,concat(pname,\' \',fname,\' \',lname) as ptname ,person.person_id as \'pid\' ,person.birthdate ,person.birthtime ,\'\' as \'@start_date\' , \'\' as \'@end_date\' \r\n from person_wbc\r\n left JOIN person on person_wbc.person_id = person.person_id\r\n inner JOIN (SELECT person_id \r\nfrom person_wbc \r\nGROUP BY person_id HAVING  count(person_wbc_id) > 1) person_wbc_chk on person_wbc.person_id = person_wbc_chk.person_id  ', '2017-04-18 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('NU9301', 'เด็ก บช.3 มีพัฒนาการสงสัยล่าช้า', 'SELECT concat(person.pname ,\'\',person.fname,\' \',person.lname) as ptname,person.cid,person.person_id  as pid ,\r\nperson.birthdate  , service_date  as \'@start_date\' ,    service_date  as \'@end_date\'\r\n                    from person_wbc\r\n                     left JOIN person on person_wbc.person_id = person.person_id\r\n                     inner JOIN (select person_id,person_wbc_service.vn,person_wbc_service.service_date\r\n                    from  person_wbc_service\r\n                    left join  person_wbc on person_wbc_service.person_wbc_id = person_wbc.person_wbc_id\r\n                    where wbc_development_assess_id = 2) person_wbc_chk on person_wbc.person_id = person_wbc_chk.person_id\r\n                    where person_wbc_chk.vn ', '2018-04-19 14:28:56');
INSERT INTO `pk_err_code_chk` VALUES ('NU9401', 'เด็ก บช.4 มีพัฒนาการสงสัยล่าช้า', 'SELECT concat(person.pname ,\'\',person.fname,\' \',person.lname) as ptname,person.cid,person.person_id  as pid ,\r\nperson.birthdate  , vaccine_date  as \'@start_date\' ,    vaccine_date  as \'@end_date\'\r\n                     from person_epi\r\n                     left JOIN person on person_epi.person_id = person.person_id\r\n                     inner JOIN (select person_id,person_epi_vaccine.vn,person_epi_vaccine.vaccine_date\r\n                    from  person_epi_vaccine\r\n                    left join  person_epi on person_epi_vaccine.person_epi_id = person_epi.person_epi_id\r\n                    where wbc_development_assess_id = 2) person_epi_chk on person_epi.person_id = person_epi_chk.person_id  \r\n                    where  person_epi_chk.vn  ', '2018-04-19 14:25:03');
INSERT INTO `pk_err_code_chk` VALUES ('NU9901', 'เด็กที่อายุ 9 เดือน 29วัน (ไม่ได้ลงข้อมูล พัฒนาการ)', 'SELECT concat(person.pname ,\'\',person.fname,\' \',person.lname) as ptname,person.cid,person.person_id  as pid ,\r\nperson.birthdate  , DATE_ADD(person.birthdate, INTERVAL 9 month)  as \'@start_date\' , \r\n   DATE_ADD((DATE_ADD(person.birthdate, INTERVAL 9 month)), INTERVAL 29 day)  as \'@end_date\' \r\nFROM person \r\nLEFT JOIN (SELECT nutrition_date ,person_wbc.person_id\r\nfrom person_wbc_nutrition\r\nLEFT JOIN person_wbc on person_wbc_nutrition.person_wbc_id = person_wbc.person_wbc_id\r\n) person_chk on person.person_id = person_chk.person_id \r\nand person_chk.nutrition_date BETWEEN DATE_ADD(person.birthdate, INTERVAL 9 month) and DATE_ADD((DATE_ADD(person.birthdate, INTERVAL 9 month)), INTERVAL 29 day)\r\nwhere  person.house_regist_type_id in(1,3) and person_chk.nutrition_date is null  ', '2017-03-20 10:51:05');
