INSERT INTO `pk_err_code_chk` VALUES ('PA9212', 'ข้อมูลสัญชาติ(NATION) ไม่พบอยู่ในรหัสมาตราฐานของสนย.', 'select hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y\r\n    from patient \r\n   where (nationality is null) or  nationality not in(select nationality FROM nationality )', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PA9216', 'ข้อมูลสถานะบุคล(Labor Type) ไม่พบอยู่ในรายการของสนย.', 'select hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y\r\n   from patient \r\n  where nationality <> 99 and labor_type not in(select person_labor_type_id from person_labor_type)', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PA9901', 'ผู้ป่วยมีข้อมูลการตายแต่ยังไม่ถูก DisCharge ตาย', 'select hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y \r\n    from patient \r\n  where (death <> \'Y\' and hn in(select hn from death) ) or (death is null  and hn in(select hn from death)) ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PA9903', 'คำนำหน้าชื่อ(pname) ไม่สัมพันธ์กับเพศ(sex)', 'select hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y \r\n    from patient \r\nwhere (sex = 2 and pname not in(select name from pname where sex = 2)) or ((sex = 1 and pname not in(select name from pname where sex = 1)))\r\n', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PA9904', 'ข้อมูลสถานะบุคคล(TYPEAREA) ไม่สัมพันธ์กับที่อยู่', 'select patient.hn,patient.cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y \r\n    from patient\r\n    INNER JOIN (SELECT patient.hn,patient.cid FROM patient \r\n		left join person on patient.cid = person.cid\r\n		where type_area = \'1\' and concat(chwpart,amppart,tmbpart) not in(\'421203\')\r\n		and person.patient_link is null) pt_error on patient.hn = pt_error.hn  ', '2016-10-20 16:43:11');
INSERT INTO `pk_err_code_chk` VALUES ('PA9905', 'ข้อมูลสถานะของ \"พระ\" หรือ \"สามเณร\"  ไม่ใช่ \"สมณะ\" ', 'select patient.hn,patient.cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y \r\n    from patient\r\n    INNER JOIN (SELECT patient.hn,patient.cid from patient \r\nwhere (pname  like\'%พระ%\'  or pname like\'%สามเณร%\' ) and marrystatus <> \'6\') pt_error on patient.hn = pt_error.hn  ', '2017-08-22 09:50:44');
INSERT INTO `pk_err_code_chk` VALUES ('PA9906', 'ข้อมูลสถานะเป็น \"สมณะ\" แต่คำนำหน้าไม่ใช่ \"พระ\" หรือ \"สามเณร\"  ', 'select patient.hn,patient.cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y \r\n    from patient\r\n    INNER JOIN (SELECT patient.hn,patient.cid from patient \r\nwhere (pname  not like\'%พระ%\'  and pname not like\'%สามเณร%\' ) and marrystatus = \'6\') pt_error on patient.hn = pt_error.hn  ', '2017-08-22 13:16:30');
INSERT INTO `pk_err_code_chk` VALUES ('PA9907', 'ข้อมูลสถานะของ \"แม่ชี\" ไม่ใช่ (\"โสด\" \"หม้าย\" \"แยก\" หรือ \"หย่า\")', 'select patient.hn,patient.cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())-year(birthday)) as  age_y \r\n    from patient\r\n    INNER JOIN (SELECT patient.hn,patient.cid from patient \r\n where   (patient.pname   like\'%แม่ชี%\' ) and patient.marrystatus not in(1,3,4,5) ) pt_error on patient.hn = pt_error.hn  ', '2017-08-22 14:02:23');
INSERT INTO `pk_err_code_chk` VALUES ('PE1101', 'รหัสสถานบริการ(PCUCODE) ไม่ครบ 5 หลัก หรือเป็นค่าว่าง(null)', '', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE1102', 'ข้อมูลเลขที่บัตรประชาชน(CID) ไม่ครบ 13 หลักหรือเป็นค่าว่าง(null)', '', '2016-07-08 11:09:22');
