INSERT INTO `pk_err_code_chk` VALUES ('PE1105', 'ไม่มีข้อมูลคำนำหน้า(PNAME)', ' select  person.person_id as person_id,person.cid,concat(person.pname,\' \',person.fname,\' \',person.lname) as ptname ,(year(CURDATE())- year(person.birthdate)) as age_y  \r\n , concat(patient.chwpart,patient.tmbpart,patient.amppart)  as hous_addr    \r\n   from person \r\n   left join patient on person.cid = patient.cid where  \r\n  (person.pname =\'\' or person.pname is null)  ', '2022-08-25 10:38:35');
INSERT INTO `pk_err_code_chk` VALUES ('PE1107', 'ข้อมูล เพศ(SEX)  เป็นค่าที่ไม่ใช่ 1 หรือ 2 หรือเป็นค่าว่าง (null)', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y  ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)  \r\nend  as hous_addr     from person\r\n where (sex not in(1,2) or sex is null) ', '2016-07-08 11:09:22');
