INSERT INTO `pk_err_code_chk` VALUES ('PE1115', 'ข้อมูลการศึกษา(EDUCATE) มีไม่ครบ 1 หลัก หรือเป็นค่าว่าง(null)', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y   ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr    from person where (education not in(select education from education)  or education is null)', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE1116', 'ข้อมูลตำแหน่ง(HOUSE POSITION) มีไม่ใช่ 1,2  หรือเป็นค่าว่าง(null)', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y  ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr    from person\r\n where (person_house_position_id not in(1,2) or person_house_position_id  is null)', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE1117', 'เลขบัตรประชาชนไม่ถูกต้องเกิดจากการ (GENERATE จาก HosXp)', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y ,\r\n      case WHEN person.house_id > 1 then  (SELECT concat(chwpart,amppart,tmbpart) as cw from hospcode where hospcode = (select hospitalcode from opdconfig))  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr     from person \r\n     where left(cid,6) = (select concat(\'0\',hospitalcode) from opdconfig) and nationality = \'99\'  and citizenship = \'99\'  ', '2016-07-15 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE1140', 'ข้อมูลสถานะบุคคล(TYPEAREA) มีค่าที่ไม่ใช่ 1-5 หรือเป็นค่าว่าง(null)', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y  ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr    from person \r\nwhere (house_regist_type_id not in(select house_regist_type_id from house_regist_type) or house_regist_type_id is null)  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE1141', 'ข้อมูลสถานะ/สาเหตุการจำหน่าย(DISCHAR) มีค่าที่ไม่ใช่ 1, 2, 3 หรือ 9 หรือเป็นค่าว่าง(null)', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y  ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr   from person \r\nwhere (person_discharge_id not in(select person_discharge_id from person_discharge) or person_discharge_id is null) ', '2016-07-08 11:09:22');
