INSERT INTO `pk_err_code_chk` VALUES ('PE9902', 'ข้อมูล  TYPE AREA เป็น 1 แต่สถานะ Discharge เป็น \'ย้าย\'', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y  ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr    from person\r\n where house_regist_type_id = 1 and person_discharge_id = 2', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE9903', 'คำนำหน้าชื่อ(pname )ไม่สัมพันธ์กับเพศ (sex)', 'select person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,age_y  ,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr     from person\r\nwhere (sex = 2 and pname not in(select name from pname where sex = 2)) or ((sex = 1 and pname not in(select name from pname where sex = 1)))\r\n', '2016-07-15 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE9904', 'ข้อมูลประชากรที่ Patient Link เป็นค่าวาง', '  select person_id,cid,concat(pname,\' \',fname,\' \',lname,\'       HN = \',(select hn from patient where cid = person.cid)) as ptname ,age_y  \r\n,\r\n case WHEN person.house_id > 1 then  \'421203\'  \r\n			when (person.house_id = 1 or person.house_id is null)  then (select concat(patient.chwpart,patient.tmbpart,patient.amppart) from patient where patient.cid = person.cid)\r\n			end  as hous_addr    from person\r\n   where (person.patient_link <>\'Y\' or person.patient_link is null)   and cid in(select cid from patient)   ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE9905', 'ข้อมูลประชากรที่อยู่ในเขต(ลงทะเบียนจากห้องบัตร) แต่ไม่ได้ถูกนำเข้า บช. 1', ' select  \'\' as person_id,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,(year(CURDATE())- year(birthday)) as age_y  \r\n   ,concat(\'เลขที่ \' ,patient.addrpart,\' หมู่  \' ,patient.moopart)  as hous_addr     from patient \r\n  where type_area in(1,3) and concat(chwpart,amppart,tmbpart) in(SELECT concat(chwpart,amppart,tmbpart) as cw from hospcode where hospcode = (select hospitalcode from opdconfig)\r\n)  and cid not in(SELECT cid from person)  ', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE9906', 'ข้อมูลประชากรที่มีเลข HN ใน บช.1 ไม่ตรงกันกับ HN ใน Patient (ห้องบัตร)', ' select  person.person_id as person_id,person.cid,concat(person.pname,\' \',person.fname,\' \',person.lname) as ptname ,(year(CURDATE())- year(person.birthdate)) as age_y  \r\n , concat(patient.chwpart,patient.tmbpart,patient.amppart)  as hous_addr    from person,patient where person.cid = patient.cid and person.patient_hn <> patient.hn  and person.death <>\'Y\'', '2016-07-08 11:09:22');
INSERT INTO `pk_err_code_chk` VALUES ('PE9907', 'ข้อมูลสถานะของ \"พระ\" หรือ \"สามเณร\" ไม่ใช่ \"สมณะ\"', ' select  person.person_id as person_id,person.cid,concat(person.pname,\' \',person.fname,\' \',person.lname) as ptname ,(year(CURDATE())- year(person.birthdate)) as age_y  \r\n , concat(patient.chwpart,patient.tmbpart,patient.amppart)  as hous_addr    from person left join patient on person.cid = patient.cid where  \r\n  (person.pname   like\'%พระ%\'  or person.pname  like\'%สามเณร%\' ) and person.marrystatus <> \'6\'  and person.death <>\'Y\'', '2017-08-22 13:29:12');
INSERT INTO `pk_err_code_chk` VALUES ('PE9908', 'ข้อมูลสถานะเป็น \"สมณะ\" แต่คำนำหน้าไม่ใช่ \"พระ\" หรือ \"สามเณร\"', ' select  person.person_id as person_id,person.cid,concat(person.pname,\' \',person.fname,\' \',person.lname) as ptname ,(year(CURDATE())- year(person.birthdate)) as age_y  \r\n , concat(patient.chwpart,patient.tmbpart,patient.amppart)  as hous_addr    from person left join patient on person.cid = patient.cid where  \r\n  (person.pname  not like\'%พระ%\'  and person.pname not like\'%สามเณร%\' ) and person.marrystatus = \'6\'  and person.death <>\'Y\'', '2017-08-22 13:29:17');
INSERT INTO `pk_err_code_chk` VALUES ('PE9909', 'ข้อมูลสถานะของ \"แม่ชี\" ไม่ใช่ (\"โสด\" \"หม้าย\" \"แยก\" หรือ \"หย่า\")', ' select  person.person_id as person_id,person.cid,concat(person.pname,\' \',person.fname,\' \',person.lname) as ptname ,(year(CURDATE())- year(person.birthdate)) as age_y  \r\n , concat(patient.chwpart,patient.tmbpart,patient.amppart)  as hous_addr    from person left join patient on person.cid = patient.cid where  \r\n  (person.pname   like\'%แม่ชี%\' ) and person.marrystatus not in(1,3,4,5) and person.death <>\'Y\'', '2017-08-22 13:56:17');
INSERT INTO `pk_err_code_chk` VALUES ('PE9910', 'ข้อมูล CID พ่อเหมือนกับ CID ตัวเอง', ' select  person.person_id as person_id,person.cid,concat(person.pname,\' \',person.fname,\' \',person.lname) as ptname ,(year(CURDATE())- year(person.birthdate)) as age_y  \r\n , concat(patient.chwpart,patient.tmbpart,patient.amppart)  as hous_addr    \r\n   from person \r\n   left join patient on person.cid = patient.cid where  \r\n  (person.cid = person.father_cid) ', '2017-10-11 14:21:17');
