INSERT INTO `pk_err_code_chk` VALUES ('SP9299', 'มีข้อมูลซ้ำซ้อน ใน visit เดียวกัน', 'select  ovst.vn,ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate , ovst.vsttime,pp_special_chk.pp_special_code\r\nfrom ovst\r\nleft join patient on ovst.hn = patient.hn  \r\nINNER JOIN (SELECT vn ,(SELECT GROUP_CONCAT(DISTINCT(pp_special_type.pp_special_code)) \r\nFROM pp_special pp1 \r\nleft JOIN pp_special_type on pp1.pp_special_type_id = pp_special_type.pp_special_type_id\r\nwhere    pp1.vn = pp_special.vn\r\nGROUP BY vn\r\nHAVING count(pp1.pp_special_type_id) > 1) as pp_special_code\r\n\r\nfrom pp_special \r\nGROUP BY vn\r\nHAVING  count(DISTINCT(pp_special_type_id)) <> COUNT(pp_special_type_id)) pp_special_chk on ovst.vn = pp_special_chk.vn \r\nwhere ovst.vn ', '2017-04-26 22:03:37');
INSERT INTO `pk_err_code_chk` VALUES ('SP9901', 'บันทึกรหัสคัดกรองมะเร็งเต้านมในผู้ชาย', 'select  ovst.vn,ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate , ovst.vsttime,pp_special_chk.pp_special_code\r\nfrom ovst\r\nleft join patient on ovst.hn = patient.hn  \r\nINNER JOIN (SELECT pp_special.vn , pp_special_type.pp_special_code\r\n from pp_special \r\n                left JOIN pp_special_type on pp_special.pp_special_type_id = pp_special_type.pp_special_type_id\r\n                left JOIN ovst_seq on pp_special.vn = ovst_seq.vn\r\n                left JOIN person on ovst_seq.pcu_person_id = person.person_id\r\n                where pp_special_type.pp_special_code in(\'1B0030\',\'1B0031\',\'1B0032\',\'1B0033\',\'1B0034\',\'1B0035\',\'1B0036\',\'1B0037\',\'1B0039\')\r\n                and person.sex = 1  and ovst_seq.vn  = pp_special.vn) pp_special_chk on ovst.vn = pp_special_chk.vn \r\n   where ovst.vn  ', '2019-04-09 15:32:36');
INSERT INTO `pk_err_code_chk` VALUES ('SP9902', 'คัดกรองมะเร็งปากมดลูกในผู้ชาย', 'select  ovst.vn,ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate , ovst.vsttime,pp_special_chk.pp_special_code\r\nfrom ovst\r\nleft join patient on ovst.hn = patient.hn  \r\nINNER JOIN (SELECT pp_special.vn , pp_special_type.pp_special_code\r\n from pp_special \r\n                left JOIN pp_special_type on pp_special.pp_special_type_id = pp_special_type.pp_special_type_id\r\n                left JOIN ovst_seq on pp_special.vn = ovst_seq.vn\r\n                left JOIN person on ovst_seq.pcu_person_id = person.person_id\r\n                where pp_special_type.pp_special_code in(\'1B0040\',\'1B0041 \',\'1B0042\',\'1B0043 \',\'1B0044\',\'1B0045\',\'1B30\',\'1B40\')\r\n                and person.sex = 1  and ovst_seq.vn  = pp_special.vn) pp_special_chk on ovst.vn = pp_special_chk.vn \r\n   where ovst.vn ', '2019-08-01 09:54:20');
INSERT INTO `pk_err_code_chk` VALUES ('SP9903', 'คัดกรองมะเร็งปากมดลูก ที่อายุน้อยกว่า 20 ปี', 'select  ovst.vn,ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate , ovst.vsttime,pp_special_chk.pp_special_code\r\nfrom ovst\r\nleft join patient on ovst.hn = patient.hn  \r\nINNER JOIN (SELECT pp_special.vn , pp_special_type.pp_special_code\r\n from pp_special \r\n                left JOIN pp_special_type on pp_special.pp_special_type_id = pp_special_type.pp_special_type_id\r\n                left JOIN ovst_seq on pp_special.vn = ovst_seq.vn\r\n                left JOIN person on ovst_seq.pcu_person_id = person.person_id\r\n                where pp_special_type.pp_special_code \r\n                in(\'1B0040\',\'1B0041 \',\'1B0042\',\'1B0043 \',\'1B0044\',\'1B0045\',\'1B30\',\'1B40\')\r\n                and year(CURDATE()) - year(person.birthdate)<20  and ovst_seq.vn  = pp_special.vn) pp_special_chk on ovst.vn = pp_special_chk.vn \r\n   where ovst.vn ', '2019-08-01 10:05:21');
INSERT INTO `pk_err_code_chk` VALUES ('SP9904', 'คัดกรองมะเร็งเต้านม ที่อายุน้อยกว่า 20 ปี', 'select  ovst.vn,ovst.hn,cid,concat(pname,\' \',fname,\' \',lname) as ptname ,ovst.vstdate as vstdate , ovst.vsttime,pp_special_chk.pp_special_code\r\nfrom ovst\r\nleft join patient on ovst.hn = patient.hn  \r\nINNER JOIN (SELECT pp_special.vn , pp_special_type.pp_special_code\r\n from pp_special \r\n                left JOIN pp_special_type on pp_special.pp_special_type_id = pp_special_type.pp_special_type_id\r\n                left JOIN ovst_seq on pp_special.vn = ovst_seq.vn\r\n                left JOIN person on ovst_seq.pcu_person_id = person.person_id\r\n                where pp_special_type.pp_special_code \r\n                in(\'1B0030\',\'1B0031\',\'1B0032\',\'1B0033\',\'1B0034\',\'1B0035\',\'1B0036\',\'1B0037\',\'1B0039\')\r\n                and year(CURDATE()) - year(person.birthdate)<20  and ovst_seq.vn  = pp_special.vn) pp_special_chk on ovst.vn = pp_special_chk.vn \r\n   where ovst.vn ', '2019-08-01 10:07:32');
INSERT INTO `pk_err_code_chk` VALUES ('SP9905', 'ลงข้อมูลการติดตามพัฒนาการล่าช้าไม่อยู่ในช่วง 14-30 วัน', '\r\nselect  patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,ovst.vstdate ,ovst.vsttime ,\r\novst.vn , (SELECT pp1.vn \r\nfrom pp_special pp1\r\nINNER JOIN ovst ov1 on  pp1.vn = ov1.vn\r\nwhere pp1.pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code in(\'1B262\',\'1B260\'))\r\nand ov1.vn > pp_special.vn and ov1.hn = ovst.hn limit 1) as diag_vn \r\n,DATEDIFF( (SELECT ov1.vstdate \r\nfrom pp_special pp1\r\nINNER JOIN ovst ov1 on  pp1.vn = ov1.vn\r\nwhere pp1.pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code in(\'1B262\',\'1B260\'))\r\nand ov1.vn > pp_special.vn and ov1.hn = ovst.hn limit 1),ovst.vstdate) as chk_date_visit\r\n\r\nfrom pp_special \r\nINNER JOIN ovst on  pp_special.vn = ovst.vn\r\nINNER JOIN patient on ovst.hn = patient.hn\r\nwhere pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code = \'1B261\' )\r\nAND DATEDIFF( (SELECT ov1.vstdate \r\nfrom pp_special pp1\r\nINNER JOIN ovst ov1 on  pp1.vn = ov1.vn\r\nwhere pp1.pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code in(\'1B262\',\'1B260\'))\r\nand ov1.vn > pp_special.vn and ov1.hn = ovst.hn limit 1),ovst.vstdate) NOT BETWEEN\'14\' and \'30\' and pp_special.vn ', '2020-02-24 16:19:36');
INSERT INTO `pk_err_code_chk` VALUES ('SP9906', 'ยังไม่ลงข้อมูลการติดตามพัฒนาการล่าช้า', 'select  patient.hn,patient.cid,concat(patient.pname,\' \',patient.fname,\' \',patient.lname) as ptname ,ovst.vstdate ,ovst.vsttime ,\r\novst.vn , (SELECT pp1.vn \r\nfrom pp_special pp1\r\nINNER JOIN ovst ov1 on  pp1.vn = ov1.vn\r\nwhere pp1.pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code in(\'1B262\',\'1B260\'))\r\nand ov1.vn > pp_special.vn and ov1.hn = ovst.hn limit 1) as diag_vn \r\n,DATEDIFF( (SELECT ov1.vstdate \r\nfrom pp_special pp1\r\nINNER JOIN ovst ov1 on  pp1.vn = ov1.vn\r\nwhere pp1.pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code in(\'1B262\',\'1B260\'))\r\nand ov1.vn > pp_special.vn and ov1.hn = ovst.hn limit 1),ovst.vstdate) as chk_date_visit , \'\' as pp_special_code\r\n\r\nfrom pp_special \r\nINNER JOIN ovst on  pp_special.vn = ovst.vn\r\nINNER JOIN patient on ovst.hn = patient.hn\r\nwhere pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code = \'1B261\' )\r\n and (SELECT pp1.vn \r\nfrom pp_special pp1\r\nINNER JOIN ovst ov1 on  pp1.vn = ov1.vn\r\nwhere pp1.pp_special_type_id in(SELECT pp_special_type_id FROM pp_special_type where pp_special_code in(\'1B262\',\'1B260\'))\r\nand ov1.vn > pp_special.vn and ov1.hn = ovst.hn limit 1) is null and pp_special.vn ', '2020-02-24 16:25:39');
